import {EventEmitter, Injectable} from '@angular/core';
import {Palette} from "../models/palette.model";
import {PaletteGenerator, PaletteScheme} from "../class/palette-generator";
import {StorageService} from "./storage.service";

@Injectable({
  providedIn: 'root'
})
export class PaletteService {

  hex: string | undefined
  scheme: PaletteScheme = PaletteScheme.RAINBOW
  private palette: Palette | undefined
  private paletteChangeEmitter: EventEmitter<Palette | undefined> = new EventEmitter()

  constructor(
    private storageService: StorageService
  ) {
    const palette = storageService.loadPalette()
    if (palette)
      this.loadPalette(palette)
    else
      this.clearPalette()
  }

  /**
   * Generate a new color palette with a specific color and scheme
   * @param hex
   * @param scheme
   */
  generatePalette(hex: string, scheme: PaletteScheme) {
    if (!hex.match(/^#[0-9A-Fa-f]{6}$/))
      throw 'Hex must be a 6-digit hex code.'

    this.hex = hex
    this.scheme = Object.values(PaletteScheme).indexOf(scheme) % 8
    const palette = PaletteGenerator.generatePalette(hex, scheme)
    this.palette = palette
    this.paletteChangeEmitter.emit(palette)
  }

  /**
   * Load an existing palette
   * @param palette
   */
  loadPalette(palette: Palette) {
    this.hex = palette.colors[0].getShade(500).hex
    this.palette = palette
    this.paletteChangeEmitter.emit(palette)
  }

  /**
   * Unload the current palette
   */
  clearPalette() {
    this.palette = undefined
    this.paletteChangeEmitter.emit(undefined)
  }

  /**
   * Return the current palette
   */
  getPalette() {
    return this.palette
  }

  /**
   * Check if a palette is present
   */
  hasPalette() {
    return !!this.palette
  }

  /**
   * Return the palette change event emitter
   */
  getPaletteChangeEmitter() {
    return this.paletteChangeEmitter
  }

}

import {EventEmitter, Injectable} from '@angular/core';
import {Palette} from "../models/palette.model";

@Injectable({
  providedIn: 'root'
})
export class StorageService {

  darkEmitter = new EventEmitter<Boolean>()

  constructor() { }

  /**
   * Load the theme if it is stored in local storage.
   * If no theme was saved, the browser default theme is used.
   */
  loadTheme() {
    if (!localStorage.getItem('theme')) {
      if (window.matchMedia('(prefers-color-scheme: dark)'))
        return this.toggleTheme(true)
      else {
        this.darkEmitter.emit(true)
        return false
      }
    } else {
      return this.toggleTheme(localStorage.getItem('theme') === 'dark')
    }
  }

  /**
   * Toggle between dark and light theme.
   * Force dark or light mode with parameter.
   * @param dark
   */
  toggleTheme(dark: boolean | undefined) {
    document.body.classList.toggle('dark', dark)

    dark = document.body.classList.contains('dark')
    localStorage.setItem('theme', dark ? 'dark' : 'light')

    this.darkEmitter.emit(dark)

    return dark
  }

  /**
   * Load the palette saved in local storage.
   * If no palette is saved a random one is going to be generated.
   */
  loadPalette(): Palette | undefined {
    const stored = localStorage.getItem('palette')
    if (stored) {
      try {
        return Palette.parsePalette(JSON.parse(stored))
      } catch (e) {
        console.error(e)
      }
    }
    return undefined
  }

  /**
   * Save palette in local storage.
   * @param palette
   */
  savePalette(palette: Palette) {
    localStorage.setItem('palette', palette.toString())
  }

}

