import {Color} from "./color.model";
// @ts-ignore
import {v4 as uuidv4} from "uuid";

export class Palette {

  title: string
  id: string
  colors: Color[] = []
  order = false

  constructor(title: string, id?: string) {
    this.title = title
    this.id = id || uuidv4()
  }

  /**
   * Add a color to the beginning of the palette.
   * @param color
   * @param front
   */
  public addColor(color: Color, front = true) {
    if (this.colors.find(c => c.name === color.name)) {
      this.colors[this.colors.findIndex(c => c.name === color.name)] = color
    } else {
      if (front)
        this.colors.unshift(color)
      else
        this.colors.push(color)
    }

    this.order = false
  }

  /**
   * Remove the color from the palette
   * @param color
   */
  public removeColor(color: Color) {
    const index = this.colors.indexOf(color)
    if (index > -1) {
      this.colors.splice(index, 1)
      return true
    } else {
      return false
    }
  }

  /**
   * Replace color with same name
   * @param color
   */
  replaceColor(color: Color) {
    let index = this.colors.findIndex(c => c.name === color.name)
    if (index < 0)
      this.addColor(color, false)
    else
      this.colors[index] = color
  }

  /**
   * Sort all colors of the palette by hue
   */
  public sortColors() {
    this.colors.sort((a, b) => {
      if (this.order)
        return a.getShade(500).hue - b.getShade(500).hue
      else
        return b.getShade(500).hue - a.getShade(500).hue
    })
    this.order = !this.order
  }

  /**
   * Stringify the palette.
   */
  public toString() {
    return JSON.stringify({
      id: this.id,
      title: this.title,
      colors: this.colors
    })
  }

  /**
   * Parse the json object to a palette.
   * Throws exception if not all values are given.
   * @param json
   */
  public static parsePalette(json: any): Palette {
    if (!json.title)
      throw 'Palette has no title'
    if (!json.id)
      throw 'Palette has no id'
    if (!json.colors)
      throw 'Palette has no colors'

    const palette = new Palette(json.title, json.id)

    for (const color of json.colors) {
      palette.addColor(Color.parseColor(color))
    }
    palette.colors.reverse()

    return palette
  }

}

import {Palette} from "../models/palette.model";
import {Color} from "../models/color.model";
import {Shade} from "../models/shade.model";

export class PaletteExporter {

  /**
   * Export a palette for usage in a separate colors.css file.
   * @param palette Palette to export
   */
  static exportCssFile(palette: Palette) {
    return `:root {\n\t${this.exportPaletteToCSS(palette).replace(/\n/g, '\n\t')}\n}`
  }

  /**
   * Export a palette for usage in plain CSS as CSS properties.
   * @param palette Palette to export
   */
  static exportPaletteToCSS(palette: Palette) {
    return `/* Color palette generated by ${window.location.origin} */\n\n${palette.colors.map(c => this.exportColorToCSS(c)).join('\n\n')}`
  }

  /**
   * Export a color for usage in plain CSS as CSS properties.
   * @param color Color to export
   * @private
   */
  private static exportColorToCSS(color: Color) {
    const name = color.name.replace(/\s+/g, '-').toLowerCase()
    return color.shades.map(s => this.exportShadeToCSS(s, name)).join('\n')
  }

  /**
   * Export a shade for usage in plain CSS as CSS property.
   * @param shade Shade to export
   * @param name Name of the color
   * @private
   */
  private static exportShadeToCSS(shade: Shade, name: string) {
    return `--${name}-${shade.index}:${shade.index < 100 ? ' ' : ''} ${shade.hex};`
  }

  /**
   * Export a palette for usage in a separate tailwind.colors.js file.
   * @param palette Palette to export
   */
  static exportTailwindFile(palette: Palette) {
    return `module.exports = {\n\t${this.exportPaletteToTailwind(palette).replace(/\n/g, '\n\t')}\n}`
  }

  /**
   * Export a palette for usage in TailwindCSS.
   * @param palette Palette to export
   */
  static exportPaletteToTailwind(palette: Palette) {
    return palette.colors.map(c => this.exportColorToTailwind(c)).join(',\n')
  }

  /**
   * Export a color for usage in TailwindCSS.
   * @param color Color to export
   * @private
   */
  private static exportColorToTailwind(color: Color) {
    const name = color.name.replace(/\s+/g, '-').toLowerCase()
    return `'${name}': {\n${color.shades.map(s => this.exportShadeToTailwind(s)).join(',\n')}\n}`
  }

  /**
   * Export a shade for usage in TailwindCSS.
   * @param shade Shade to export
   * @private
   */
  private static exportShadeToTailwind(shade: Shade) {
    return `\t${shade.index}:${shade.index < 100 ? ' ' : ''} '${shade.hex}'`
  }

}

import {Shade} from "../models/shade.model";
import {Palette} from "../models/palette.model";
import {Color} from "../models/color.model";
import {ColorNamer} from "./color-namer";

export class PaletteGenerator {

  static generatePalette(hex: string, scheme: PaletteScheme): Palette {
    const shade = new Shade(-1, true, hex)

    const values = Object.values(PaletteScheme)
    const value = values.indexOf(scheme) % (values.length / 2)

    switch (value) {
      case PaletteScheme.MONOCHROMATIC:
        return this.generateMonochromaticPalette(shade)
      case PaletteScheme.ANALOGOUS:
        return this.generateAnalogousPalette(shade)
      case PaletteScheme.COMPLEMENTARY:
        return this.generateComplementaryPalette(shade)
      case PaletteScheme.SPLIT:
        return this.generateSplitPalette(shade)
      case PaletteScheme.TRIADIC:
        return this.generateTriadicPalette(shade)
      case PaletteScheme.COMPOUND:
        return this.generateCompoundPalette(shade)
      case PaletteScheme.RAINBOW:
        return this.generateRainbowPalette(shade)
      default:
        const schemes = Object.keys(PaletteScheme)
        const index = schemes[Math.floor(Math.random() * schemes.length)]
        // @ts-ignore
        return this.generatePalette(hex, PaletteScheme[index])
    }
  }

  private static generateMonochromaticPalette(shade: Shade) {
    const monochromatic = new Palette('Monochrom')

    monochromatic.addColor(new Color('primary', [shade]))
    monochromatic.addColor(new Color('muted', [
      new Shade(-1, true, shade.hue, 30, 50)
    ]), false)
    monochromatic.addColor(new Color('gray', [
      new Shade(-1, true, shade.hue, 2, 50)
    ]), false)

    return monochromatic
  }

  private static generateAnalogousPalette(shade: Shade) {
    const analogous = new Palette('Analogous')

    analogous.addColor(new Color('primary', [shade]))

    analogous.addColor(new Color('secondary', [
      new Shade(-1, true, this.changeHueOnWheel(shade.hue, 315), Math.max(shade.saturation - 20, 0), 40)
    ]), false)
    analogous.addColor(new Color('secondary muted', [
      new Shade(-1, true, this.changeHueOnWheel(shade.hue, 270), 25, 20)
    ]), false)

    analogous.addColor(new Color('accent', [
      new Shade(-1, true, this.changeHueOnWheel(shade.hue, 45), shade.saturation, 50)
    ]), false)
    analogous.addColor(new Color('accent muted', [
      new Shade(-1, true, this.changeHueOnWheel(shade.hue, 90) % 360, 25, 20)
    ]), false)

    return analogous
  }

  private static generateComplementaryPalette(shade: Shade) {
    const complementary = new Palette('Complementary')

    complementary.addColor(new Color('primary', [shade]))

    complementary.addColor(new Color('gray', [
      new Shade(-1, true, shade.hue, 3, 50)
    ]), false)

    complementary.addColor(new Color('secondary', [
      new Shade(-1, true, this.changeHueOnWheel(shade.hue, 180), Math.max(shade.saturation - 20, 0), 40)
    ]), false)

    return complementary
  }

  private static generateSplitPalette(shade: Shade) {
    const split = new Palette('Split')

    split.addColor(new Color('primary', [shade]))

    split.addColor(new Color('secondary', [
      new Shade(-1, true, this.changeHueOnWheel(shade.hue, 20), Math.max(shade.saturation - 20, 0), 40)
    ]), false)
    split.addColor(new Color('gray', [
      new Shade(-1, true, this.changeHueOnWheel(shade.hue, 20), 3, 50)
    ]), false)

    split.addColor(new Color('accent', [
      new Shade(-1, true, this.changeHueOnWheel(shade.hue, 180), shade.saturation, 80)
    ]), false)

    return split
  }

  private static generateTriadicPalette(shade: Shade) {
    const triadic = new Palette('Triadic')

    triadic.addColor(new Color('primary', [shade]))

    triadic.addColor(new Color('primary muted', [
      new Shade(-1, true, shade.hue, 20, 30)
    ]), false)

    triadic.addColor(new Color('secondary', [
      new Shade(-1, true, this.changeHueOnWheel(shade.hue, 120), Math.max(shade.saturation - 20, 0), 40)
    ]), false)
    triadic.addColor(new Color('secondary muted', [
      new Shade(-1, true, this.changeHueOnWheel(shade.hue, 120), 20, 30)
    ]), false)

    triadic.addColor(new Color('accent', [
      new Shade(-1, true, this.changeHueOnWheel(shade.hue, 240), shade.saturation, 20)
    ]), false)

    return triadic
  }

  private static generateCompoundPalette(shade: Shade) {
    const compound = new Palette('Compound')

    compound.addColor(new Color('primary', [shade]))

    compound.addColor(new Color('secondary', [
      new Shade(-1, true, this.changeHueOnWheel(shade.hue, 210), Math.max(shade.saturation - 20, 0), 40)
    ]), false)

    compound.addColor(new Color('accent', [
      new Shade(-1, true, this.changeHueOnWheel(shade.hue, 150), shade.saturation, 50)
    ]), false)

    return compound
  }

  private static generateRainbowPalette(shade: Shade) {
    const rainbow = new Palette('Rainbow')

    rainbow.addColor(new Color('primary', [shade]))

    const hues = [4, 26, 55, 95, 149, 200, 253]
    let currentHue = hues.reduce((prev, curr) =>
      (Math.abs(curr - shade.hue) < Math.abs(prev - shade.hue) ? curr : prev))
    if (currentHue === 253 && shade.hue > 308)
      currentHue = 4

    hues.forEach(hue => {
      if (hue !== currentHue) {
        let newHue = (shade.hue + (hue - currentHue) + 360) % 360
        const newShade = new Shade(-1, true,
          newHue,
          Math.min(100, Math.max(0, shade.saturation - 20 + Math.floor(Math.random() * 40))),
          Math.min(100, Math.max(0, shade.luminosity - 20 + Math.floor(Math.random() * 40)))
        )
        rainbow.addColor(new Color(ColorNamer.nameColor(newShade), [newShade]), false)
      }
    })

    return rainbow
  }

  private static changeHueOnWheel(hue: number, change: number) {
    let wheel
    if (hue < 60)
      wheel = 2 * hue
    else if (hue < 120)
      wheel = hue + 60
    else if (hue < 240)
      wheel = .5 * hue + 120
    else
      wheel = hue

    wheel += change
    wheel %= 360

    let newHue
    if (wheel < 120)
      newHue = .5 * wheel
    else if (wheel < 180)
      newHue = wheel + 300
    else if (wheel < 240)
      newHue = 2 * wheel + 120
    else
      newHue = wheel

    return newHue % 360
  }

}

export enum PaletteScheme {
  RAINBOW,
  SURPRISE_ME,
  MONOCHROMATIC,
  ANALOGOUS,
  COMPLEMENTARY,
  SPLIT,
  TRIADIC,
  COMPOUND
}

import {ColorConverter} from "../class/color-converter";

export class Shade {

  index: number
  hex: string
  hue: number
  saturation: number
  luminosity: number
  brightness: number = 0
  fixed: boolean

  public constructor(index: number, fixed: boolean, hex: string)
  public constructor(index: number, fixed: boolean, hue: number, saturation: number, luminosity: number)
  public constructor(index: number, fixed: boolean, hex: string, hue: number, saturation: number, luminosity: number)

  constructor(...args: any[]) {
    this.index = args[0]
    this.fixed = args[1]

    if (args.length === 3) {
      if (!args[2].match(/^#[0-9A-Fa-f]{6}$/))
        throw `Color ${args[2]} is not in form #RRGGBB.`

      this.hex = args[2].toUpperCase()
      const hsl = ColorConverter.HEXtoHSL(this.hex)
      this.hue = hsl.hue
      this.saturation = hsl.saturation
      this.luminosity = hsl.luminosity
      this.updateBrightness()
    } else if (args.length === 5) {
      this.hue = args[2]
      this.saturation = args[3]
      this.luminosity = args[4]
      this.hex = ColorConverter.HSLtoHEX(this.hue, this.saturation, this.luminosity)
      this.updateBrightness()
    } else {
      if (!args[2].match(/^#[0-9A-Fa-f]{6}$/))
        throw `Color ${args[2]} is not in form #RRGGBB.`

      this.hex = args[2].toUpperCase()
      this.hue = args[3]
      this.saturation = args[4]
      this.luminosity = args[5]
      this.updateBrightness()
    }
  }

  /**
   * Set the shades index
   * @param index
   */
  public setIndex(index: number) {
    this.index = index
  }

  /**
   * Set the shades HEX value and update all other properties
   * @param hex
   * @param fixed
   */
  public setHEX(hex: string, fixed = false) {
    if (!hex.match(/^#[0-9A-Fa-f]{6}$/))
      throw `Color ${hex} is not in form #RRGGBB.`

    this.fixed = fixed
    this.hex = hex.toUpperCase()
    const hsl = ColorConverter.HEXtoHSL(this.hex)
    this.hue = hsl.hue
    this.saturation = hsl.saturation
    this.luminosity = hsl.luminosity
    this.updateBrightness()
  }

  /**
   * Set the shades HSL values and update all other properties
   * @param hue
   * @param saturation
   * @param luminosity
   * @param fixed
   */
  public setHSL(hue: number, saturation: number, luminosity: number, fixed = false) {
    this.fixed = fixed
    this.hue = hue
    this.saturation = saturation
    this.luminosity = luminosity
    this.hex = ColorConverter.HSLtoHEX(this.hue, this.saturation, this.luminosity)
    this.updateBrightness()
  }

  /**
   * Update perceived brightness
   * @private
   */
  private updateBrightness() {
    const rgb = ColorConverter.HEXtoRGB(this.hex)
    this.brightness = Math.round(
      Math.sqrt(
        .299 * rgb.red * rgb.red +
        .587 * rgb.green * rgb.green +
        .114 * rgb.blue * rgb.blue
      ) / 2.55
    )
  }

  /**
   * Generate a random shade.
   */
  public static generateRandomShade(): Shade {
    return new Shade(
      0,
      true,
      Math.floor(Math.random() * 360),
      30 + Math.floor(Math.random() * 60),
      25 + Math.floor(Math.random() * 50)
    )
  }

  /**
   * Parse the json object to a shade.
   * Throws exception if json object is no valid shade object.
   * @param json
   */
  public static parseShade(json: any): Shade {
    if ((!json.index && json.index !== 0)
        || !json.hex
        || (!json.hue && json.hue !== 0)
        || (!json.saturation && json.saturation !== 0)
        || (!json.luminosity && json.luminosity !== 0))
      throw 'Not all parameters for shade are set'

    return new Shade(json.index, json.fixed, json.hex.toUpperCase(), json.hue, json.saturation, json.luminosity)
  }

}

import {EventEmitter} from "@angular/core";
import {Dialog} from "../interfaces/dialog.interface";
import {toUnicodeVariant} from "../utils/to-unicode-variant.util";

export class TailwindCopyDialog {

  constructor(
    private notification: EventEmitter<Dialog | undefined>,
  ) { }

  getNotification(): Dialog {
    const closeEmitter = new EventEmitter()
    closeEmitter.subscribe(() => {
      this.notification.emit(undefined)
    })

    const docEmitter = new EventEmitter()
    docEmitter.subscribe(() => {
      window.open('https://tailwindcss.com/docs/customizing-colors#color-object-syntax', '_blank')
    })

    return {
      id: 'export-tailwind-copy',
      interpolateParams: {
        config: toUnicodeVariant('tailwind.config.js', 'm')
      },
      actions: [{
        id: 'more',
        action: docEmitter
      }]
    }
  }

}

import {EventEmitter} from "@angular/core";
import {PaletteExporter} from "../class/palette-exporter";
import {Palette} from "../models/palette.model";
import {Dialog} from "../interfaces/dialog.interface";
import {TailwindCopyDialog} from "./tailwind-copy.dialog";
import {TailwindFileDialog} from "./tailwind-file.dialog";
import {toUnicodeVariant} from "../utils/to-unicode-variant.util";

export class TailwindDialog {

  constructor(
    private notification: EventEmitter<Dialog | undefined>,
    private palette: Palette,
  ) { }

  getNotification(): Dialog {
    const tailwindCopyEmitter = new EventEmitter()
    tailwindCopyEmitter.subscribe(() => {
      const tailwind = PaletteExporter.exportPaletteToTailwind(this.palette)
      navigator.clipboard.writeText(tailwind).then(() => {
        this.notification.emit(new TailwindCopyDialog(
          this.notification
        ).getNotification())
      }).catch(e => {
        this.notification.emit({
          id: 'copy-error',
          interpolateParams:{
            error: e
          }
        })
      })
    })

    const tailwindFileEmitter = new EventEmitter()
    tailwindFileEmitter.subscribe(() => {
      const tailwind = PaletteExporter.exportTailwindFile(this.palette)
      const blob = new Blob([tailwind], {type: 'text/javascript'})

      const a = document.createElement('a')
      a.href = URL.createObjectURL(blob)
      a.download = 'tailwind.colors.js'
      a.click()

      this.notification.emit(new TailwindFileDialog(
        this.notification
      ).getNotification())
    })

    return {
      id: 'export-tailwind',
      interpolateParams: {
        file: toUnicodeVariant('tailwind.colors.js', 'm'),
      },
      actions: [{
        id: 'copy',
        action: tailwindCopyEmitter
      }, {
        id: 'file',
        action: tailwindFileEmitter
      }]
    }
  }

}

import {EventEmitter} from "@angular/core";
import {Dialog} from "../interfaces/dialog.interface";
import { toUnicodeVariant } from "../utils/to-unicode-variant.util";

export class TailwindFileDialog {

  constructor(
    private notification: EventEmitter<Dialog | undefined>,
  ) { }

  getNotification(): Dialog {
    const closeEmitter = new EventEmitter()
    closeEmitter.subscribe(() => {
      this.notification.emit(undefined)
    })

    const docEmitter = new EventEmitter()
    docEmitter.subscribe(() => {
      window.open('https://tailwindcss.com/docs/customizing-colors#using-the-default-colors', '_blank')
    })

    return {
      id: 'export-tailwind-file',
      interpolateParams: {
        file: toUnicodeVariant('tailwind.colors.js', 'm'),
        config: toUnicodeVariant('tailwind.config.js', 'm'),
        'import': toUnicodeVariant('colors: require(\'./tailwind.colors\'),', 'm')
      },
      actions: [{
        id: 'more',
        action: docEmitter
      }]
    }
  }

}

