import {Component} from '@angular/core';
import {environment} from "../environments/environment";
import {StorageService} from "./services/storage.service";
import {PaletteService} from "./services/palette.service";
import {Router} from "@angular/router";
import {ColorNamer} from "./class/color-namer";
import {TranslateService} from "@ngx-translate/core";
import {Title} from "@angular/platform-browser";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
})
export class AppComponent {

  title = 'Rainbow Palette | Get your own color palette from just a single color'
  version = environment.version
  dark

  navigation = [{
    link: '/',
    id: 'home',
    icon: '<path d="M2.25 4.125c0-1.036.84-1.875 1.875-1.875h5.25c1.036 0 1.875.84 1.875 1.875V17.25a4.5 4.5 0 11-9 0V4.125zm4.5 14.25a1.125 1.125 0 100-2.25 1.125 1.125 0 000 2.25z" />' +
      '<path d="M10.719 21.75h9.156c1.036 0 1.875-.84 1.875-1.875v-5.25c0-1.036-.84-1.875-1.875-1.875h-.14l-8.742 8.743c-.09.089-.18.175-.274.257zM12.738 17.625l6.474-6.474a1.875 1.875 0 000-2.651L15.5 4.787a1.875 1.875 0 00-2.651 0l-.1.099V17.25c0 .126-.003.251-.01.375z" />',
  }, {
    link: '/edit',
    id: 'edit',
    icon: '<path d="M18.75 12.75h1.5a.75.75 0 000-1.5h-1.5a.75.75 0 000 1.5zM12 6a.75.75 0 01.75-.75h7.5a.75.75 0 010 1.5h-7.5A.75.75 0 0112 6zM12 18a.75.75 0 01.75-.75h7.5a.75.75 0 010 1.5h-7.5A.75.75 0 0112 18zM3.75 6.75h1.5a.75.75 0 100-1.5h-1.5a.75.75 0 000 1.5zM5.25 18.75h-1.5a.75.75 0 010-1.5h1.5a.75.75 0 010 1.5zM3 12a.75.75 0 01.75-.75h7.5a.75.75 0 010 1.5h-7.5A.75.75 0 013 12zM9 3.75a2.25 2.25 0 100 4.5 2.25 2.25 0 000-4.5zM12.75 12a2.25 2.25 0 114.5 0 2.25 2.25 0 01-4.5 0zM9 15.75a2.25 2.25 0 100 4.5 2.25 2.25 0 000-4.5z" />',
  }, {
    link: '/preview',
    id: 'preview',
    icon: '<path fill-rule="evenodd" d="M1.5 7.125c0-1.036.84-1.875 1.875-1.875h6c1.036 0 1.875.84 1.875 1.875v3.75c0 1.036-.84 1.875-1.875 1.875h-6A1.875 1.875 0 011.5 10.875v-3.75zm12 1.5c0-1.036.84-1.875 1.875-1.875h5.25c1.035 0 1.875.84 1.875 1.875v8.25c0 1.035-.84 1.875-1.875 1.875h-5.25a1.875 1.875 0 01-1.875-1.875v-8.25zM3 16.125c0-1.036.84-1.875 1.875-1.875h5.25c1.036 0 1.875.84 1.875 1.875v2.25c0 1.035-.84 1.875-1.875 1.875h-5.25A1.875 1.875 0 013 18.375v-2.25z" clip-rule="evenodd" />',
  }]

  constructor(
    private storage: StorageService,
    public paletteService: PaletteService,
    public router: Router,
    private translate: TranslateService,
    private titleService: Title
  ) {
    this.titleService.setTitle(this.title)

    // Load theme from local storage and subscribe to changes
    this.dark = storage.loadTheme()
    this.storage.darkEmitter.subscribe(d => this.dark = d.valueOf())

    ColorNamer.loadDictionary()

    // Setup translation pipe
    this.translate.setDefaultLang('en')
    this.translate.use(this.translate.getBrowserLang() ?? 'en').subscribe(_ => {
      this.title = `Rainbow Palette | ${ this.translate.instant('app.title') }`
      this.titleService.setTitle(this.title)
    })
  }

}

import {Component, Input, OnInit} from '@angular/core';
import {Color} from "../../models/color.model";
import {Shade} from "../../models/shade.model";
import {ChangeType, ColorService} from "../../services/color.service";

@Component({
  selector: 'color-editor',
  templateUrl: './color-editor.component.html',
  styleUrls: ['./color-editor.component.css']
})
export class ColorEditorComponent implements OnInit {

  @Input()
  dark = false

  shade: Shade | undefined
  color: Color | undefined

  constructor(
    public colorService: ColorService
  ) {
    this.colorService.getColorChangeEmitter().subscribe(changeType => {
      if (changeType === ChangeType.LOAD) {
        this.color = this.colorService.getColor()
        this.shade = this.colorService.getShade()
      }

      this.updateProperties()
    })
  }

  ngOnInit(): void {
    this.updateProperties()
  }

  /**
   * Update current shade. The property with type is changed to the new value.
   * @param type Property to change
   * @param value Value to change to
   */
  updateColor(type: UpdateType, value: string | number) {
    if (!this.shade)
      return

    if (type === UpdateType.HEX && isNaN(+value)) {
      if (`${value}`.match(/^#[0-9A-Fa-f]{6}$/))
        this.shade.setHEX(`${value}`, true)
      else
        return
    } else if (!isNaN(+value)) {
      value = parseInt(`${value}`)
      if (type === UpdateType.HUE)
        this.shade.setHSL(this.wheelToHue(value), this.shade.saturation, this.shade.luminosity, true)
      else if (type === UpdateType.SATURATION)
        this.shade.setHSL(this.shade.hue, value, this.shade.luminosity, true)
      else if (type === UpdateType.LUMINOSITY)
        this.shade.setHSL(this.shade.hue, this.shade.saturation, 100 - value, true)
    }

    this.colorService.adjustShades()
  }

  /**
   * Update all css properties to the values of the current selected shade.
   */
  updateProperties() {
    if (!this.shade)
      return

    document.documentElement.style.setProperty('--selected-hex', this.shade.hex)
    document.documentElement.style.setProperty('--selected-hue', String(this.shade.hue))
    document.documentElement.style.setProperty('--selected-saturation', this.shade.saturation + "%")
    document.documentElement.style.setProperty('--selected-luminosity', this.shade.luminosity + "%")
  }

  /**
   * Change a shade to be edited
   * @param shadeIndex
   */
  changeShade(shadeIndex: number) {
    if (this.color)
      this.colorService.loadColor(this.color, shadeIndex)
  }

  /**
   * Release a shade and let it be interpolated instead
   * @param shade
   * @param $event
   */
  releaseShade(shade: Shade, $event: MouseEvent) {
    $event.preventDefault()
    if (!this.color)
      return

    if (this.color.shades.filter(s => s.fixed).length > 1) {
      shade.fixed = false
      this.colorService.adjustShades()
    }
  }

  /**
   * Calculate from color wheel degree to hue
   * @param wheel
   */
  wheelToHue(wheel: number) {
    let newHue
    if (wheel < 120)
      newHue = .5 * wheel
    else if (wheel < 180)
      newHue = wheel + 300
    else if (wheel < 240)
      newHue = 2 * wheel + 120
    else
      newHue = wheel

    return newHue % 360
  }

  /**
   * Calculate from hue to color wheel degree
   * @param hue
   */
  hueToWheel(hue: number) {
    let wheel
    if (hue < 60)
      wheel = 2 * hue
    else if (hue < 120)
      wheel = hue + 60
    else if (hue < 240)
      wheel = .5 * hue + 120
    else
      wheel = hue

    return wheel % 360
  }

}

enum UpdateType {
  HEX, HUE, SATURATION, LUMINOSITY
}

