import { MatomoRouterInterceptor } from '@ngx-matomo/router'
import { Injectable } from '@angular/core'
import { StorageService } from '../services/storage.service'
import { MatomoTracker } from '@ngx-matomo/tracker'
import { Observable } from 'rxjs'
import { NavigationEnd } from '@angular/router'

@Injectable()
export class AnalyticsInterceptor implements MatomoRouterInterceptor {
  isPWA = false

  constructor(private tracker: MatomoTracker, private storage: StorageService) {
    // Check for installed PWA
    if (
      window.matchMedia('(display-mode: standalone)').matches ||
      // eslint-disable-next-line  @typescript-eslint/ban-ts-comment
      // @ts-ignore
      window.navigator.standalone
    ) {
      this.isPWA = true
    }

    window.addEventListener('appinstalled', () => {
      this.isPWA = true
      this.tracker.trackEvent('pwa', 'installed')
    })
  }

  beforePageTrack(
    event: NavigationEnd
  ): Observable<void> | Promise<void> | void {
    this.tracker.setDocumentTitle(event.url)

    this.tracker.setCustomDimension(1, this.storage.language)
    this.tracker.setCustomDimension(2, this.storage.dark ? 'dark' : 'light')
    this.tracker.setCustomDimension(3, this.isPWA ? 'pwa' : 'web')
  }
}

import { EventEmitter, Injectable } from '@angular/core'
import { Color } from '../models/color.model'
import { ColorInterpolater } from '../class/color-interpolater'
import { Shade } from '../models/shade.model'
import { PaletteService } from './palette.service'

@Injectable({
  providedIn: 'root',
})
export class ColorService {
  private color?: Color
  private shade?: Shade
  private colorChangeEmitter = new EventEmitter<ChangeType>()

  constructor(private paletteService: PaletteService) {}

  /**
   * Open the editor with a copy of the color and optional a specific shade.
   * @param color
   * @param shadeIndex
   */
  loadColor(color: Color, shadeIndex?: number): void {
    this.color = Color.parseColor(color)
    if (shadeIndex) this.shade = this.color.getShade(shadeIndex)
    else this.shade = this.color.shades.find((s) => s.fixed)
    this.colorChangeEmitter.emit(ChangeType.LOAD)
  }

  /**
   * Close the color editor
   */
  closeEditor(): void {
    this.color = undefined
    this.shade = undefined
    this.colorChangeEmitter.emit(ChangeType.LOAD)
  }

  /**
   * Adjust the shades of a color
   */
  adjustShades(): void {
    if (this.color) {
      ColorInterpolater.regenerateShades(this.color)
      this.colorChangeEmitter.emit(ChangeType.ADJUST)
    }
  }

  /**
   * Save all changes made to the color
   */
  saveColor(): void {
    if (this.color) {
      this.paletteService.getPalette()?.replaceColor(this.color)
      this.closeEditor()
    }
  }

  /**
   * Return the current color
   */
  getColor(): Color | undefined {
    return this.color
  }

  /**
   * Return the current shade
   */
  getShade(): Shade | undefined {
    return this.shade
  }

  /**
   * Return the color change event emitter
   */
  getColorChangeEmitter(): EventEmitter<ChangeType> {
    return this.colorChangeEmitter
  }
}

export enum ChangeType {
  LOAD,
  ADJUST,
}

