import { ColorInterpolater } from '../class/color-interpolater'
import { Shade } from './shade.model'
import { ColorNamer } from '../class/color-namer'

export class Color {
  name: string
  shades: Shade[]

  public constructor(name: string, hex: string)
  public constructor(name: string, hexes: string[])
  public constructor(name: string, shades: Shade[])

  // eslint-disable-next-line  @typescript-eslint/no-explicit-any
  constructor(...args: any[]) {
    this.name = args[0].startsWith('#') ? args[0].substring(1) : args[0]

    if (typeof args[1] === 'string') {
      this.shades = []
      const shade = new Shade(-1, true, args[1])
      this.shades.push(shade)
    } else if (typeof args[1][0] === 'string') {
      this.shades = []

      for (const hex of args[1]) {
        this.shades.push(new Shade(-1, true, hex))
      }
    } else {
      this.shades = args[1]
    }

    ColorInterpolater.regenerateShades(this)
  }

  /**
   * Get shade by index (50, 100, 200, ..., 900)
   * @param index
   */
  public getShade(index: number): Shade {
    return this.shades.find((s) => s.index === index) || this.shades[0]
  }

  /**
   * Generate a random color with all shades.
   */
  public static generateRandomColor(): Color {
    const shade = Shade.generateRandomShade()
    return new Color(ColorNamer.nameColor(shade), shade.hex)
  }

  /**
   * Stringify the color.
   */
  public toString() {
    return JSON.stringify({
      name: this.name,
      shades: this.shades,
    })
  }

  /**
   * Parse the json object to a color.
   * Throws exception if json object is no valid color object.
   * @param json
   */
  // eslint-disable-next-line  @typescript-eslint/no-explicit-any
  public static parseColor(json: any): Color {
    if (!json.name) throw 'Color has no name'
    if (!json.shades || json.shades.length < 1)
      throw 'Color has not enough shades'

    const shades: Shade[] = []
    for (const shade of json.shades) {
      shades.push(Shade.parseShade(shade))
    }

    return new Color(json.name, shades)
  }
}

export class ColorConverter {
  static HEXtoRGB(hex: string) {
    if (!hex.match(/^#[0-9A-Fa-f]{6}$/))
      throw `Color ${hex} is not in form #RRGGBB.`

    return {
      red: parseInt(hex.substring(1, 3), 16),
      green: parseInt(hex.substring(3, 5), 16),
      blue: parseInt(hex.substring(5, 7), 16),
    }
  }

  static HEXtoHSL(hex: string) {
    const rgb = this.HEXtoRGB(hex)
    return this.RGBtoHSL(rgb.red, rgb.green, rgb.blue)
  }

  static HEXtoHSV(hex: string) {
    const hsl = this.HEXtoHSL(hex)
    return this.HSLtoHSV(hsl.hue, hsl.saturation, hsl.luminosity)
  }

  static HEXtoCMYK(hex: string) {
    const rgb = this.HEXtoRGB(hex)
    return this.RGBtoCMYK(rgb.red, rgb.green, rgb.blue)
  }

  static RGBtoHEX(red: number, green: number, blue: number) {
    if (
      red < 0 ||
      red > 255 ||
      green < 0 ||
      green > 255 ||
      blue < 0 ||
      blue > 255
    )
      throw `rgb(${red}, ${green}, ${blue}) is not in valid format.`

    return `#${(red < 16 ? 0 : '') + red.toString(16).toUpperCase()}${
      (green < 16 ? 0 : '') + green.toString(16).toUpperCase()
    }${(blue < 16 ? 0 : '') + blue.toString(16).toUpperCase()}`
  }

  static RGBtoHSL(red: number, green: number, blue: number) {
    if (
      red < 0 ||
      red > 255 ||
      green < 0 ||
      green > 255 ||
      blue < 0 ||
      blue > 255
    )
      throw `rgb(${red}, ${green}, ${blue}) is not in valid format.`

    const r = red / 255
    const g = green / 255
    const b = blue / 255

    const cMax = Math.max(r, g, b)
    const cMin = Math.min(r, g, b)
    const delta = cMax - cMin

    const luminosity = (cMax + cMin) * 50
    const saturation =
      delta === 0
        ? 0
        : (100 * delta) / (1 - Math.abs(2 * (luminosity / 100) - 1))

    let hue
    if (delta === 0) hue = 0
    else if (cMax === r) hue = 60 * (((g - b) / delta) % 6)
    else if (cMax === g) hue = 60 * ((b - r) / delta + 2)
    else hue = 60 * ((r - g) / delta + 4)

    if (hue < 0) hue += 360

    return {
      hue: Math.round(hue),
      saturation: Math.round(saturation),
      luminosity: Math.round(luminosity),
    }
  }

  static RGBtoHSV(red: number, green: number, blue: number) {
    const hsl = this.RGBtoHSL(red, green, blue)
    return this.HSLtoHSV(hsl.hue, hsl.saturation, hsl.luminosity)
  }

  static RGBtoCMYK(red: number, green: number, blue: number) {
    if (
      red < 0 ||
      red > 255 ||
      green < 0 ||
      green > 255 ||
      blue < 0 ||
      blue > 255
    )
      throw `rgb(${red}, ${green}, ${blue}) is not in valid format.`

    const r = red / 255
    const g = green / 255
    const b = blue / 255

    const k = 1 - Math.max(r, g, b)
    const c = (1 - r - k) / (1 - k)
    const m = (1 - g - k) / (1 - k)
    const y = (1 - b - k) / (1 - k)

    return {
      cyan: Math.round(c * 100),
      magenta: Math.round(m * 100),
      yellow: Math.round(y * 100),
      key: Math.round(k * 100),
    }
  }

  static HSLtoHEX(hue: number, saturation: number, luminosity: number): string {
    const rgb = this.HSLtoRGB(hue, saturation, luminosity)
    return this.RGBtoHEX(rgb.red, rgb.green, rgb.blue)
  }

  static HSLtoRGB(hue: number, saturation: number, luminosity: number) {
    if (
      hue < 0 ||
      hue > 360 ||
      saturation < 0 ||
      saturation > 100 ||
      luminosity < 0 ||
      luminosity > 100
    )
      throw `Color values [${hue}, ${saturation}%, ${luminosity}%] are not in valid ranges.`

    const h = hue
    const s = saturation / 100
    const l = luminosity / 100

    const c = (1 - Math.abs(2 * l - 1)) * s
    const x = c * (1 - Math.abs(((h / 60) % 2) - 1))
    const m = l - c / 2

    const r = h < 60 || h >= 300 ? c : h < 120 || h >= 240 ? x : 0
    const g = h >= 240 ? 0 : h < 60 || h >= 180 ? x : c
    const b = h < 120 ? 0 : h < 180 || h >= 300 ? x : c

    return {
      red: Math.round((r + m) * 255),
      green: Math.round((g + m) * 255),
      blue: Math.round((b + m) * 255),
    }
  }

  static HSLtoHSV(hue: number, saturation: number, luminosity: number) {
    if (
      hue < 0 ||
      hue > 360 ||
      saturation < 0 ||
      saturation > 100 ||
      luminosity < 0 ||
      luminosity > 100
    )
      throw `Color values [${hue}, ${saturation}%, ${luminosity}%] are not in valid ranges.`

    const v = luminosity + saturation * Math.min(luminosity, 1 - luminosity)

    const s = v === 0 ? 0 : 2 * (1 - luminosity / v)

    return {
      hue: hue,
      saturation: Math.round(s),
      value: Math.round(v),
    }
  }

  static HSLtoCMYK(hue: number, saturation: number, luminosity: number) {
    const rgb = this.HSLtoRGB(hue, saturation, luminosity)
    return this.RGBtoCMYK(rgb.red, rgb.green, rgb.blue)
  }

  static HSVtoHEX(hue: number, saturation: number, value: number) {
    const rgb = this.HSVtoRGB(hue, saturation, value)
    return this.RGBtoHEX(rgb.red, rgb.green, rgb.blue)
  }

  static HSVtoRGB(hue: number, saturation: number, value: number) {
    const hsl = this.HSVtoHSL(hue, saturation, value)
    return this.HSLtoRGB(hsl.hue, hsl.saturation, hsl.luminosity)
  }

  static HSVtoHSL(hue: number, saturation: number, value: number) {
    if (
      hue < 0 ||
      hue > 360 ||
      saturation < 0 ||
      saturation > 100 ||
      value < 0 ||
      value > 100
    )
      throw `Color values [${hue}, ${saturation}%, ${value}%] are not in valid ranges.`

    const l = value * (1 - saturation / 2)

    const s = l === 0 || l === 1 ? 0 : (value - l) / Math.min(l, 1 - l)

    return {
      hue: hue,
      saturation: Math.round(s),
      luminosity: Math.round(l),
    }
  }

  static HSVtoCMYK(hue: number, saturation: number, value: number) {
    const rgb = this.HSVtoRGB(hue, saturation, value)
    return this.RGBtoCMYK(rgb.red, rgb.green, rgb.blue)
  }

  static CMYKtoHEX(cyan: number, magenta: number, yellow: number, key: number) {
    const rgb = this.CMYKtoRGB(cyan, magenta, yellow, key)
    return this.RGBtoHEX(rgb.red, rgb.green, rgb.blue)
  }

  static CMYKtoRGB(cyan: number, magenta: number, yellow: number, key: number) {
    if (
      cyan < 0 ||
      cyan > 100 ||
      magenta < 0 ||
      magenta > 100 ||
      yellow < 0 ||
      yellow > 100 ||
      key < 0 ||
      key > 100
    )
      throw `[${cyan}, ${magenta}, ${yellow}, ${key}] is not in valid format.`

    const c = cyan / 100
    const m = magenta / 100
    const y = yellow / 100
    const k = key / 100

    const r = (1 - c) * (1 - k)
    const g = (1 - m) * (1 - k)
    const b = (1 - y) * (1 - k)

    return {
      red: Math.round(255 * r),
      green: Math.round(255 * g),
      blue: Math.round(255 * b),
    }
  }

  static CMYKtoHSL(cyan: number, magenta: number, yellow: number, key: number) {
    const rgb = this.CMYKtoRGB(cyan, magenta, yellow, key)
    return this.RGBtoHSL(rgb.red, rgb.green, rgb.blue)
  }

  static CMYKtoHSV(cyan: number, magenta: number, yellow: number, key: number) {
    const rgb = this.CMYKtoRGB(cyan, magenta, yellow, key)
    return this.RGBtoHSV(rgb.red, rgb.green, rgb.blue)
  }
}

import { Shade } from '../models/shade.model'
import { Color } from '../models/color.model'

export class ColorInterpolater {
  /**
   * Regenerate every shade but the fixed ones.
   * @param color
   */
  public static regenerateShades(color: Color) {
    // Get all fixed shades sorted by perceived brightness
    const shades = [...color.shades]
      .filter((shade) => shade.fixed)
      .sort((a, b) => b.brightness - a.brightness)

    // Give each shade an index
    const indices = [...Array(10).keys()].map((i) => i * 100)
    indices[0] = 50
    this.distributeIndices(shades, indices)

    // Calculate minimal saturation
    const minimum = this.calculateMinSaturation(shades)

    // Create "white"
    const white = this.createBorderShade(shades[0], minimum, true)
    shades.unshift(white)

    // Create "black"
    const black = this.createBorderShade(
      shades[shades.length - 1],
      minimum,
      false
    )
    shades.push(black)

    // Create missing shades
    for (const index of indices) {
      // Shade already exists
      if (shades.some((shade) => shade.index === index)) continue

      // Find darker, lighter and third point
      const lighter =
        shades.findLast((shade) => shade.index < index && shade.fixed) || white
      const darker =
        shades.find((shade) => shade.index > index && shade.fixed) || black
      let third = shades.findLast(
        (shade) => shade.index < lighter.index && shade.fixed
      )
      if (!third)
        third =
          shades.find((shade) => shade.index > darker.index && shade.fixed) ||
          black

      // Interpolate properties
      const hue =
        this.mapNumbers(
          index,
          lighter.index,
          darker.index,
          darker.hue - lighter.hue > 180 ? lighter.hue + 360 : lighter.hue,
          lighter.hue - darker.hue > 180 ? darker.hue + 360 : darker.hue
        ) % 360
      const luminosity = this.mapNumbers(
        index,
        lighter.index,
        darker.index,
        lighter.luminosity,
        darker.luminosity
      )
      const saturation = this.calculateSaturation(
        luminosity,
        lighter,
        darker,
        third
      )

      // Add shade
      const shade = new Shade(index, false, hue, saturation, luminosity)
      shades.push(shade)
    }

    // Filter "black" and "white" and sort again by index
    color.shades = shades
      .filter((shade) => !(shade.index === 0 || shade.index === 1200))
      .sort((a, b) => a.index - b.index)
  }

  /**
   * Distribute fixed indices from `[50, 100, 200, ..., 900]` to all shades by perceived brightness.
   * Every index will only appear once.
   * This is done by shifting a duplicate index to the next one.
   *
   * **Help wanted:** This algorithm could be improved by calculating penalty points for shifting, generating multiple variants and choosing the one with the fewest penalty points.
   * @param shades
   * @param indices
   */
  private static distributeIndices(shades: Shade[], indices: number[]) {
    shades.forEach((shade) => {
      const mapBrightness = Math.max(
        Math.min(1250 - 12.5 * shade.brightness, 999),
        1
      )
      const index = indices.reduce((prev, curr) =>
        Math.abs(mapBrightness - curr) < Math.abs(mapBrightness - prev)
          ? curr
          : prev
      )
      shade.setIndex(index)
    })

    this.offsetIndicesFromLightest(shades)
  }

  /**
   * Offset indices from lightest to darkest so no index appears multiple times.
   * @param shades
   */
  private static offsetIndicesFromLightest(shades: Shade[]) {
    let minIndex = 0
    for (let i = 0; i < shades.length; i++) {
      const shade = shades[i]

      if (minIndex >= shade.index) {
        if (minIndex < 900) {
          shade.setIndex(minIndex + 100 - (minIndex % 100))
        } else {
          shade.setIndex(900)
          this.offsetIndicesFromDarkest(shades, i)
        }
      }

      minIndex = shade.index
    }
  }

  /**
   * Offset indices from darkest to lightest so the index of `shades[start]` is the biggest and no index appears multiple times.
   * @param shades
   * @param start
   */
  private static offsetIndicesFromDarkest(shades: Shade[], start: number) {
    let maxIndex = shades[start].index
    for (let j = start - 1; j >= 0; j--) {
      const brighter = shades[j]

      if (maxIndex > brighter.index) return

      brighter.setIndex((maxIndex -= 100))
    }
  }

  /**
   * Calculate the lowest saturation using the current lowest offset with an adjusted one.
   *
   * Returns an object including the calculated saturation alongside the brightness of the shade with the lowest saturation.
   * @param shades
   */
  private static calculateMinSaturation(shades: Shade[]) {
    const shadeWithLowestSaturation = shades.reduce((prev, curr) =>
      curr.saturation < prev.saturation ? curr : prev
    )
    const differenceFromMiddleSquared = Math.pow(
      50 - shadeWithLowestSaturation.brightness,
      2
    )
    const evenLowerSaturation = Math.max(
      shadeWithLowestSaturation.saturation - 0.01 * differenceFromMiddleSquared,
      0
    )
    return {
      saturation: Math.round(
        (shadeWithLowestSaturation.saturation + evenLowerSaturation) / 2
      ),
      brightness: shadeWithLowestSaturation.brightness,
    }
  }

  /**
   * Creates the most extreme shades ("black" and "white").
   * This is done with respect to the hue of the current lightest and darkest shades alongside the minimal saturation and brightness of the current color.
   * @param shade
   * @param minimum
   * @param white
   * @private
   */
  private static createBorderShade(
    shade: Shade,
    minimum: { saturation: number; brightness: number },
    white: boolean
  ) {
    const index = white ? 0 : 1000
    const hue = this.calculateHue(shade, white)
    const saturation = this.calculateMaxSaturation(minimum, shade, index)
    const luminosity = (1000 - index) / 10

    return new Shade(white ? 0 : 1200, true, hue, saturation, luminosity)
  }

  /**
   * Calculate the hue of the lightest and darkest shades with the hue of the nearest neighbor in mind.
   * If we want to create a lighter shade, we rotate in direction 60, 180 and 300 degree respectively.
   * If we want to create a darker shade, we rotate in direction 0, 120 and 240 degree respectively.
   * @param shade
   * @param white
   */
  private static calculateHue(shade: Shade, white: boolean) {
    const hueAdjustmentDirection =
      shade.hue < 60 ||
      (120 < shade.hue && shade.hue < 180) ||
      (240 < shade.hue && shade.hue < 300)
        ? 1
        : -1

    let hue =
      shade.hue +
      (hueAdjustmentDirection *
        (white ? 1 : -1) *
        (white ? shade.index : 1000 - shade.index)) /
        60

    if (white) {
      if ((shade.hue <= 60 && hue > 60) || (shade.hue >= 60 && hue < 60)) {
        hue = 60
      } else if (
        (shade.hue <= 180 && hue > 180) ||
        (shade.hue >= 180 && hue < 180)
      ) {
        hue = 180
      } else if (
        (shade.hue <= 300 && hue > 300) ||
        (shade.hue >= 300 && hue < 300)
      ) {
        hue = 300
      }
    } else {
      if (
        (((shade.hue > 340 && shade.hue < 360) || shade.hue === 0) &&
          hue > 0 &&
          hue < 20) ||
        (shade.hue >= 0 && shade.hue < 20 && hue < 0)
      ) {
        hue = 0
      } else if (
        (shade.hue <= 120 && hue > 120) ||
        (shade.hue >= 120 && hue < 120)
      ) {
        hue = 120
      } else if (
        (shade.hue <= 240 && hue > 240) ||
        (shade.hue >= 240 && hue < 240)
      ) {
        hue = 240
      }
    }

    return Math.round(360 + hue) % 360
  }

  /**
   * Calculate the highest saturation based on a neighbor with minimal saturation and the nearest neighbor.
   * @param minimum
   * @param neighbor
   * @param index
   */
  private static calculateMaxSaturation(
    minimum: { saturation: number; brightness: number },
    neighbor: Shade,
    index: number
  ) {
    if (neighbor.brightness === minimum.brightness)
      return Math.round(
        -0.01 * Math.pow(minimum.saturation, 2) + 2 * minimum.saturation
      )

    const a =
      (neighbor.saturation - minimum.saturation) /
      Math.pow(neighbor.brightness - minimum.brightness, 2)
    const maxSaturation =
      a * Math.pow(index / 10 - minimum.brightness, 2) + minimum.saturation

    const minAdd =
      -0.01 * Math.pow(neighbor.saturation, 2) + 2 * neighbor.saturation

    return Math.min(Math.round(Math.max(maxSaturation, minAdd, 0)), 100)
  }

  /**
   * Map numbers on a linear 1 dimensional scale
   * @param x
   * @param in_min
   * @param in_max
   * @param out_min
   * @param out_max
   */
  private static mapNumbers(
    x: number,
    in_min: number,
    in_max: number,
    out_min: number,
    out_max: number
  ): number {
    return Math.round(
      ((x - in_min) * (out_max - out_min)) / (in_max - in_min) + out_min
    )
  }

  /**
   * Calculate the saturation by interpolating a value for the current luminosity on a quadratic 2 dimensional parable defined by three points (shades).
   * @param luminosity
   * @param left
   * @param middle
   * @param right
   */
  private static calculateSaturation(
    luminosity: number,
    left: Shade,
    middle: Shade,
    right: Shade
  ) {
    const ll2 = left.luminosity * left.luminosity
    const ml2 = middle.luminosity * middle.luminosity
    const rl2 = right.luminosity * right.luminosity

    const delta =
      ll2 * middle.luminosity +
        left.luminosity * rl2 +
        ml2 * right.luminosity -
        middle.luminosity * rl2 -
        ll2 * right.luminosity -
        left.luminosity * ml2 || 0.01
    const aNumerator =
      left.saturation * middle.luminosity +
      left.luminosity * right.saturation +
      middle.saturation * right.luminosity -
      middle.luminosity * right.saturation -
      left.saturation * right.luminosity -
      left.luminosity * middle.saturation
    const bNumerator =
      ll2 * middle.saturation +
      left.saturation * rl2 +
      ml2 * right.saturation -
      middle.saturation * rl2 -
      ll2 * right.saturation -
      left.saturation * ml2
    const cNumerator =
      ll2 * middle.luminosity * right.saturation +
      left.luminosity * middle.saturation * rl2 +
      left.saturation * ml2 * right.luminosity -
      left.saturation * middle.luminosity * rl2 -
      ll2 * middle.saturation * right.luminosity -
      left.luminosity * ml2 * right.saturation

    const x = aNumerator / delta
    const y = bNumerator / delta
    const z = cNumerator / delta

    const saturation = x * luminosity * luminosity + y * luminosity + z
    return Math.max(Math.min(Math.round(saturation), 100), 0)
  }
}

import { Shade } from '../models/shade.model'
import { HttpClient, HttpXhrBackend } from '@angular/common/http'

export class ColorNamer {
  private static colorDictionary: Entry[] = []
  private static grayDictionary: Entry[] = []

  public static loadDictionary() {
    const http = new HttpClient(
      new HttpXhrBackend({
        build: () => new XMLHttpRequest(),
      })
    )

    http
      .get('/assets/color_dictionary.csv', { responseType: 'text' })
      .subscribe((data) => {
        const list = data.split('\n')
        list.shift()

        this.colorDictionary = list.map((entry) => {
          const split = entry.split(';')
          return {
            name: split[0],
            hue: parseInt(split[1]),
            saturation: parseInt(split[2]),
            luminosity: parseInt(split[3]),
          }
        })

        this.grayDictionary.filter((color) => color.hue === -1)
      })
  }

  public static nameColor(shade: Shade) {
    if (this.colorDictionary.length + this.grayDictionary.length === 0) {
      console.info('Color dictionary is not loaded (yet).')
      return shade.hex.substring(1)
    }

    const dictionary =
      shade.saturation === 0 ? this.grayDictionary : this.colorDictionary
    const nearest = dictionary.reduce((prev, curr) =>
      this.calculateDifference(curr, shade) <
      this.calculateDifference(prev, shade)
        ? curr
        : prev
    )
    return nearest.name.replace(
      /(\w)(\w*)/g,
      (g0, g1, g2) => g1.toUpperCase() + g2.toLowerCase()
    )
  }

  private static calculateDifference(
    entry: { hue: number; saturation: number; luminosity: number },
    shade: Shade
  ) {
    return (
      10 * Math.abs(entry.hue - shade.hue) +
      5 * Math.abs(entry.saturation - shade.saturation) +
      Math.abs(entry.luminosity - shade.luminosity)
    )
  }
}

interface Entry {
  name: string
  hue: number
  saturation: number
  luminosity: number
}

