import {
  Component,
  ElementRef,
  EventEmitter,
  Input,
  Output,
  ViewChild,
} from '@angular/core'
import { Color } from '../../models/color.model'
import { ColorService } from '../../services/color.service'
import { Shade } from '../../models/shade.model'
import { toUnicodeVariant } from '../../utils/to-unicode-variant.util'
import { NotificationService } from '../../services/notification.service'
import { TranslateService } from '@ngx-translate/core'

@Component({
  selector: 'app-color-viewer',
  templateUrl: './color-viewer.component.html',
})
export class ColorViewerComponent {
  @Input()
  color: Color | undefined
  @Input()
  dark = false

  @Output()
  onRemove = new EventEmitter<Color>()

  editingState = false

  @ViewChild('editName')
  editName: ElementRef<HTMLInputElement> | undefined

  constructor(
    public colorService: ColorService,
    private notificationService: NotificationService,
    private translate: TranslateService
  ) {}

  /**
   * Open editor with the shade with the given index
   * @param shadeIndex
   */
  editShade(shadeIndex: number) {
    if (this.color) this.colorService.loadColor(this.color, shadeIndex)
  }

  /**
   * Open color name editor
   */
  openEditor() {
    this.editingState = true
    setTimeout(() => {
      this.editName?.nativeElement.focus()
    }, 0)
  }

  /**
   * Close color name editor
   */
  closeEditor() {
    this.editingState = false
    if (this.color)
      this.color.name =
        this.editName?.nativeElement.value || this.translate.instant('random')
  }

  /**
   * Copy a shades hex to clipboard.
   * @param shade
   * @param $event
   */
  copyToClipboard(shade: Shade, $event: MouseEvent) {
    $event.preventDefault()
    navigator.clipboard
      .writeText(shade.hex)
      .then(() => {
        this.notificationService.notification.emit({
          id: 'copied',
          interpolateParams: {
            color: toUnicodeVariant(shade.hex, 'm'),
          },
        })
      })
      .catch((e) => {
        console.error('Error while copying to clipboard: ', e)
        this.notificationService.notification.emit('copy-error')
      })
  }
}

import { Component, Input } from '@angular/core'
import { NotificationService } from '../../services/notification.service'
import { Dialog } from '../../interfaces/dialog.interface'
import { MatomoTracker } from '@ngx-matomo/tracker'

@Component({
  selector: 'app-custom-dialog',
  templateUrl: './dialog.component.html',
})
export class DialogComponent {
  @Input()
  dark = false

  content: Dialog | undefined

  constructor(
    private notificationService: NotificationService,
    private tracker: MatomoTracker
  ) {
    notificationService.dialog.subscribe((dialogContent) => {
      this.content = dialogContent

      if (
        dialogContent &&
        /export-(css|tailwind)-(copy|file)/.test(dialogContent.id)
      ) {
        const [category, action, name] = dialogContent.id.split('-')
        this.tracker.trackEvent(category, action, name)
      }
    })
  }

  closeNotification() {
    this.notificationService.dialog.emit(undefined)
  }
}

import { Component } from '@angular/core'
import { Palette } from '../../models/palette.model'
import { PaletteService } from '../../services/palette.service'

@Component({
