import { HttpClient } from '@angular/common/http';
import { Injectable, inject } from '@angular/core';
import { firstValueFrom } from 'rxjs';
import { Shade } from '../model/shade.model';
import { HSLObject } from '../types/color-format';
import { ToastService } from './toast.service';

interface ColorMapEntry {
  hue: number;
  saturation: number;
  lightness: number;
  name: string;
}

@Injectable({
  providedIn: 'root'
})
export class ColorNameService {
  private readonly _http = inject(HttpClient);
  private readonly _toastService = inject(ToastService);

  private colorDictionary?: Array<ColorMapEntry>;

  public async getColorName(shade: Shade): Promise<string> {
    if (!this.colorDictionary) {
      await this.loadColorMap();
    }

    if (!this.colorDictionary) {
      return shade.hex.substring(1);
    }

    const name = this.colorDictionary
      .filter((entry) => {
        if (shade.hsl.S === 0) {
          return entry.hue === -1;
        } else {
          return entry.hue !== -1;
        }
      })
      .map((entry) => ({
        name: entry.name,
        distance: this.calculateDistance(shade.hsl, entry)
      }))
      .sort((a, b) => a.distance - b.distance)
      .map((entry) => entry.name)
      .at(0);

    if (!name) {
      console.warn('No color name found for', shade.hex);
      return shade.hex.substring(1);
    }

    return name.replace(/(\w)(\w*)/g, (_, first, rest) => first.toUpperCase() + rest.toLowerCase());
  }

  private async loadColorMap(): Promise<void> {
    try {
      const data = await firstValueFrom(this._http.get('/assets/color-dictionary.csv', { responseType: 'text' }));

      this.colorDictionary = data
        .split('\n')
        .filter((_, index) => index > 0)
        .map((line) => line.split(';'))
        .map((entry) => ({
          name: entry[0],
          hue: parseInt(entry[1], 10),
          saturation: parseInt(entry[2], 10),
          lightness: parseInt(entry[3], 10)
        }));
    } catch (error) {
      this._toastService.showToast({
        type: 'error',
        message: 'toast.error.load-color-map'
      });
    }
  }

  private calculateDistance(hsl: HSLObject, entry: ColorMapEntry): number {
    return (
      10 * Math.abs(hsl.H - entry.hue) + 5 * Math.abs(hsl.S - entry.saturation) + Math.abs(hsl.L - entry.lightness)
    );
  }
}

export class ColorNameServiceMock {
  public async getColorName(shade: Shade): Promise<string> {
    return shade.hex.substring(1);
  }
}

import { Color } from '../model/color.model';
import { Palette } from '../model/palette.model';
import { Shade } from '../model/shade.model';

export interface Formatter {
  filename: string;
  mimeType: string;

  formatFile(palette: Palette): string;

  formatPalette(palette: Palette): string;

  formatColor(color: Color): string;

  formatShade(shade: Shade, name: string): string;
}

export type Language = 'en' | 'de';

/**
 * A language option for the layout options.
 */
export interface LanguageOption {
  /**
   * The value of the language option.
   */
  value: Language;

  /**
   * The label of the language option.
   */
  label: string;

  /**
   * The flag of the language option.
   */
  flag: string;
}

export interface NavigationEntry {
  /**
   * The title of the navigation entry.
   * This can also be a translation key.
   */
  title: string;

  /**
   * The path to navigate to.
   */
  path: string;

  /**
   * The icon to display next to the title.
   * This has to be an icon from an `@ng-icons` integration or a custom SVG.
   */
  icon: string;

  /**
   * The description of the navigation entry.
   * This can also be a translation key.
   * This is shown as a tooltip on hover.
   */
  description: string;
}

import { IconType } from '@ng-icons/core';

/**
 * A theme type.
 */
export type Theme = 'light' | 'dark';

/**
 * A theme option for the layout options.
 */
export interface ThemeOption {
  /*
   * The value of the theme option.
   */
  value: Theme;

  /**
   * The label of the theme option.
   */
  label: string;

  /**
   * The icon of the theme option.
   */
  icon: IconType;
}

export const ToastTimeouts = {
  success: 3000,
  error: 10000,
  warning: 5000,
  info: 5000,
  default: 5000,
  test: 10
};

/**
 * The interface for the toast.
 */
export interface Toast {
  /**
   * The type of the toast.
   * The type is used to determine the timeout and styling of the toast.
   */
  type?: keyof typeof ToastTimeouts;
  /**
   * The message to be displayed in the toast.
   * It can be a string or a translation key.
   */
  message: string;
  /**
   * The parameters to be used in the translation.
   */
  parameters?: Record<string, unknown>;
}

