import { Injectable, Signal, effect, inject, signal } from '@angular/core';
import { MatomoTracker } from 'ngx-matomo-client';
import { ExportFormat } from '../constants/export-format';
import { PaletteScheme } from '../constants/palette-scheme';
import { LocalStorageKey } from '../enums/local-storage-keys';
import { TrackingEventAction, TrackingEventCategory, TrackingEventName } from '../enums/tracking-event';
import { ExportOption } from '../types/export-option';
import { LanguageService } from './language.service';
import { OfflineService } from './offline.service';
import { ThemeService } from './theme.service';
import { ToastService } from './toast.service';
import { VersionService } from './version.service';

export enum CustomDimension {
  LANGUAGE = 1,
  THEME = 2,
  PWA = 3,
  VERSION = 4
}

export enum AnalyticsStatus {
  UNSET = 'UNSET',
  ACCEPTED = 'ACCEPTED',
  DECLINED = 'DECLINED'
}

type TrackingEvent = {
  category: TrackingEventCategory;
  action: TrackingEventAction;
  name?: TrackingEventName;
  value?: number;
  timestamp: number;
};

@Injectable({
  providedIn: 'root'
})
export class AnalyticsService {
  private readonly _tracker = inject(MatomoTracker);
  private readonly _themeService = inject(ThemeService);
  private readonly _languageService = inject(LanguageService);
  private readonly _versionService = inject(VersionService);
  private readonly _offlineService = inject(OfflineService);
  private readonly _toastService = inject(ToastService);

  private readonly _status = signal<AnalyticsStatus>(AnalyticsStatus.UNSET);

  public get status(): Signal<AnalyticsStatus> {
    return this._status.asReadonly();
  }

  public constructor() {
    this._setup();

    effect(() => {
      const status = this._status();
      if (status === AnalyticsStatus.UNSET) {
        return;
      }

      const analytics = JSON.stringify({
        status,
        expiry: Date.now() + 1000 * 60 * 60 * 24 * 90
      });
      localStorage.setItem(LocalStorageKey.ANALYTICS, analytics);

      if (status === AnalyticsStatus.ACCEPTED) {
        this._tracker.setConsentGiven();
        this._processEvents('disabled');
      } else if (status === AnalyticsStatus.DECLINED) {
        this._tracker.forgetConsentGiven();
      }
    });

    // Send a heartbeat every 30 seconds
    this._tracker.enableHeartBeatTimer(30);

    // Track current theme
    effect(() => {
      this._tracker.setCustomDimension(CustomDimension.THEME, this._themeService.theme());
    });

    // Track current language
    effect(() => {
      this._tracker.setCustomDimension(CustomDimension.LANGUAGE, this._languageService.language());
    });

    // Track app version
    this._tracker.setCustomDimension(CustomDimension.VERSION, this._versionService.appVersion);

    effect(async () => {
      if (!this._offlineService.isOffline()) {
        this._processEvents('offline');
      }
    });
  }

  private _setup(): void {
    const analytics = localStorage.getItem(LocalStorageKey.ANALYTICS);
    if (analytics) {
      try {
        const parsed = JSON.parse(analytics);
        if (parsed.expiry < Date.now()) {
          localStorage.removeItem(LocalStorageKey.ANALYTICS);
        } else {
          if (parsed.status === AnalyticsStatus.ACCEPTED) {
            this._status.set(AnalyticsStatus.ACCEPTED);
          } else if (parsed.status === AnalyticsStatus.DECLINED) {
            this._status.set(AnalyticsStatus.DECLINED);
          }
        }
      } catch (error) {
        console.error('Failed to parse analytics status', error);
      }
    }

    let userId = localStorage.getItem(LocalStorageKey.USER_ID);
    if (!userId) {
      // Generate random user ID with 16 hex characters
      userId = Array.from({ length: 16 }, () => Math.floor(Math.random() * 16).toString(16)).join('');
      localStorage.setItem(LocalStorageKey.USER_ID, userId);
    }
    this._tracker.setUserId(userId);
  }

  private _queueEvent(event: Omit<TrackingEvent, 'timestamp'>, type: 'disabled' | 'offline'): void {
    const key = type === 'disabled' ? LocalStorageKey.EVENTS_DISABLED : LocalStorageKey.EVENTS_OFFLINE;
    const events = JSON.parse(localStorage.getItem(key) || '[]') as Array<TrackingEvent>;

    events.push({ ...event, timestamp: Date.now() });
    localStorage.setItem(key, JSON.stringify(events));
  }

  private _processEvents(type: 'disabled' | 'offline'): void {
    const key = type === 'disabled' ? LocalStorageKey.EVENTS_DISABLED : LocalStorageKey.EVENTS_OFFLINE;
    const cache = localStorage.getItem(key);
    if (!cache) {
      return;
    }

    const events = JSON.parse(cache) as Array<TrackingEvent>;
    for (const event of events) {
      // Skip events older than 24 hours
      if (event.timestamp < Date.now() - 1000 * 60 * 60 * 24) {
        continue;
      }

      this._tracker.trackEvent(event.category, event.action, event.name);
    }

    if (key === LocalStorageKey.EVENTS_DISABLED) {
      this.trackEvent(
        TrackingEventCategory.EVENTS_DISABLED,
        TrackingEventAction.EVENTS_DISABLED,
        undefined,
        events.length
      );
    } else {
      this.trackEvent(
        TrackingEventCategory.EVENTS_OFFLINE,
        TrackingEventAction.EVENTS_OFFLINE,
        undefined,
        events.length
      );
    }

    localStorage.removeItem(key);
  }

  public setIsPwa(isPwa: boolean): void {
    this._tracker.setCustomDimension(CustomDimension.PWA, isPwa ? 'PWA' : 'Web');
  }

  public acceptAnalytics(): void {
    this._status.set(AnalyticsStatus.ACCEPTED);
    this._toastService.showToast({
      type: 'info',
      message: 'toast.info.analytics-accepted'
    });
  }

  public declineAnalytics(): void {
    this._status.set(AnalyticsStatus.DECLINED);
  }

  /**
   * Track a custom event
   * @param category Event category
   * @param action Event action
   * @param name Event name
   */
  public trackEvent(
    category: TrackingEventCategory,
    action: TrackingEventAction,
    name?: TrackingEventName,
    value?: number
  ): void {
    if (this._status() !== AnalyticsStatus.ACCEPTED) {
      this._queueEvent(
        {
          category,
          action,
          name,
          value
        },
        'disabled'
      );
      return;
    }

    if (this._offlineService.isOffline()) {
      this._queueEvent({ category, action, name, value }, 'offline');
      return;
    }

    this._tracker.trackEvent(category, action, name, value);
  }

  /**
   * Track the export of a palette
   * @param format Format to export
   * @param option Export method
   */
  public trackPaletteExport(format: ExportFormat, option: ExportOption): void {
    const action =
      option === 'copy' ? TrackingEventAction.EXPORT_PALETTE_COPY : TrackingEventAction.EXPORT_PALETTE_FILE;

    let name: TrackingEventName | undefined;
    switch (format) {
      case ExportFormat.CSS:
        name = TrackingEventName.EXPORT_PALETTE_CSS;
        break;
      case ExportFormat.SCSS:
        name = TrackingEventName.EXPORT_PALETTE_SCSS;
        break;
      case ExportFormat.LESS:
        name = TrackingEventName.EXPORT_PALETTE_LESS;
        break;
      case ExportFormat.TAILWIND:
        name = TrackingEventName.EXPORT_PALETTE_TAILWIND;
        break;
      default:
        console.warn('Unknown palette export format', format);
        name = TrackingEventName.EXPORT_PALETTE_UNKNOWN;
    }

    this.trackEvent(TrackingEventCategory.EXPORT_PALETTE, action, name);
  }

  /**
   * Track the generation of a palette
   * @param scheme Palette scheme
   */
  public trackPaletteGeneration(scheme: PaletteScheme): void {
    let name: TrackingEventName | undefined;
    switch (scheme) {
      case PaletteScheme.RAINBOW:
        name = TrackingEventName.GENERATE_PALETTE_RAINBOW;
        break;
      case PaletteScheme.SURPRISE:
        name = TrackingEventName.GENERATE_PALETTE_SURPRISE;
        break;
      case PaletteScheme.MONOCHROME:
        name = TrackingEventName.GENERATE_PALETTE_MONOCHROME;
        break;
      case PaletteScheme.ANALOGOUS:
        name = TrackingEventName.GENERATE_PALETTE_ANALOGOUS;
        break;
      case PaletteScheme.COMPLEMENTARY:
        name = TrackingEventName.GENERATE_PALETTE_COMPLEMENTARY;
        break;
      case PaletteScheme.SPLIT_COMPLEMENTARY:
        name = TrackingEventName.GENERATE_PALETTE_SPLIT_COMPLEMENTARY;
        break;
      case PaletteScheme.TRIADIC:
        name = TrackingEventName.GENERATE_PALETTE_TRIADIC;
        break;
      case PaletteScheme.COMPOUND:
        name = TrackingEventName.GENERATE_PALETTE_COMPOUND;
        break;
      default:
        console.warn('Unknown palette generation scheme', scheme);
        name = TrackingEventName.GENERATE_PALETTE_UNKNOWN;
    }

    this.trackEvent(TrackingEventCategory.GENERATE_PALETTE, TrackingEventAction.GENERATE_PALETTE, name);
  }
}

export class AnalyticsServiceMock {
  public readonly status = signal<AnalyticsStatus>(AnalyticsStatus.UNSET).asReadonly();

  public setIsPwa(_isPwa: boolean): void {}

  public acceptAnalytics(): void {}

  public declineAnalytics(): void {}

  public trackEvent(_category: TrackingEventCategory, _action: TrackingEventAction, _name?: TrackingEventName): void {}

  public trackPaletteExport(_format: ExportFormat, _option: ExportOption): void {}

  public trackPaletteGeneration(_scheme: PaletteScheme): void {}
}

import { Dialog } from '@angular/cdk/dialog';
import { Injectable, inject, signal } from '@angular/core';
import { firstValueFrom, tap } from 'rxjs';
import { Color } from '../../shared/model';
import { sleep } from '../../shared/utils/sleep';

@Injectable({
  providedIn: 'root'
})
export class ColorEditorService {
  private readonly _dialog = inject(Dialog);
  private readonly _isModalOpen = signal(false);

  public async openColorEditor(color: Color, shadeIndex?: number): Promise<Color | undefined> {
    if (this._isModalOpen()) {
      return;
    }
    this._isModalOpen.set(true);

    const editor = await import('../editor.component').then((c) => c.EditorComponent);
    const dialogRef = this._dialog.open<Color | undefined>(editor, {
      backdropClass: 'rp-modal-backdrop',
      data: {
        color,
        shadeIndex
      },
      disableClose: true,
      panelClass: 'rp-modal-panel'
    });

    return await firstValueFrom(dialogRef.closed.pipe(tap(() => this._isModalOpen.set(false))));
  }
}

export class ColorEditorServiceMock {
  public async openColorEditor(color: Color, _shadeIndex?: number): Promise<Color | undefined> {
    await sleep(10);

    return color.copy();
  }
}

import { HttpClient } from '@angular/common/http';
import { Injectable, inject } from '@angular/core';
import { firstValueFrom } from 'rxjs';
import { Shade } from '../model/shade.model';
import { HSLObject } from '../types/color-format';
import { ToastService } from './toast.service';

interface ColorMapEntry {
  hue: number;
  saturation: number;
  lightness: number;
  name: string;
}

@Injectable({
  providedIn: 'root'
})
export class ColorNameService {
  private readonly _http = inject(HttpClient);
  private readonly _toastService = inject(ToastService);

  private colorDictionary?: Array<ColorMapEntry>;

  public async getColorName(shade: Shade): Promise<string> {
    if (!this.colorDictionary) {
      await this.loadColorMap();
    }

    if (!this.colorDictionary) {
      return shade.hex.substring(1);
    }

    const name = this.colorDictionary
      .filter((entry) => {
        if (shade.hsl.S === 0) {
          return entry.hue === -1;
        } else {
          return entry.hue !== -1;
        }
      })
      .map((entry) => ({
        name: entry.name,
        distance: this.calculateDistance(shade.hsl, entry)
      }))
      .sort((a, b) => a.distance - b.distance)
      .map((entry) => entry.name)
      .at(0);

    if (!name) {
      console.warn('No color name found for', shade.hex);
      return shade.hex.substring(1);
    }

    return name.replace(/(\w)(\w*)/g, (_, first, rest) => first.toUpperCase() + rest.toLowerCase());
  }

  private async loadColorMap(): Promise<void> {
    try {
      const data = await firstValueFrom(this._http.get('/assets/color-dictionary.csv', { responseType: 'text' }));

      this.colorDictionary = data
        .split('\n')
        .filter((_, index) => index > 0)
        .map((line) => line.split(';'))
        .map((entry) => ({
          name: entry[0],
          hue: parseInt(entry[1], 10),
          saturation: parseInt(entry[2], 10),
          lightness: parseInt(entry[3], 10)
        }));
    } catch (error) {
      this._toastService.showToast({
        type: 'error',
        message: 'toast.error.load-color-map'
      });
    }
  }

  private calculateDistance(hsl: HSLObject, entry: ColorMapEntry): number {
    return (
      10 * Math.abs(hsl.H - entry.hue) + 5 * Math.abs(hsl.S - entry.saturation) + Math.abs(hsl.L - entry.lightness)
    );
  }
}

export class ColorNameServiceMock {
  public async getColorName(shade: Shade): Promise<string> {
    return shade.hex.substring(1);
  }
}

import { Injectable, inject } from '@angular/core';
import { Value } from '../model';
import { Color } from '../model/color.model';
import { Shade } from '../model/shade.model';
import { ColorNameService } from './color-name.service';

@Injectable({
  providedIn: 'root'
})
export class ColorService {
  private readonly _colorNameService = inject(ColorNameService);

  public async randomColor(): Promise<Color> {
    const shade = Shade.random();
    const name = await this._colorNameService.getColorName(shade);
    const color = new Color([Shade.random()], name);

    this.regenerateShades(color);

    return color;
  }

  /**
   * Regenerate every shade but the fixed ones.
   * @param color
   */
  public regenerateShades(color: Color): void {
    // Get all fixed shades sorted by perceived brightness
    const shades = color.shades
      .map((shade) => shade.copy())
      .filter((shade) => shade.fixed)
      .sort((a, b) => b.perceivedBrightness - a.perceivedBrightness);

    // Give each shade an index
    const indices = [...Array(10).keys()].map((i) => i * 100);
    indices[0] = 50;
    this._distributeIndices(shades, indices);

    // Calculate minimal saturation
    const minimum = this._calculateMinSaturation(shades);

    // Create "white"
    const white = this._createBorderShade(shades[0], minimum, true);
    shades.unshift(white);

    // Create "black"
    const black = this._createBorderShade(shades[shades.length - 1], minimum, false);
    shades.push(black);

    // Create missing shades
    for (const index of indices) {
      // Shade already exists
      if (shades.some((shade) => shade.index === index)) continue;

      // Find darker, lighter and third point
      const lighter =
        shades
          .slice()
          .reverse()
          .find((shade) => shade.index < index && shade.fixed) || white;
      const darker = shades.find((shade) => shade.index > index && shade.fixed) || black;
      let third = shades
        .slice()
        .reverse()
        .find((shade) => shade.index < lighter.index && shade.fixed);
      if (!third) third = shades.find((shade) => shade.index > darker.index && shade.fixed) || black;

      // Interpolate properties
      const hue =
        this._mapNumbers(
          index,
          lighter.index,
          darker.index,
          darker.hsl.H - lighter.hsl.H > 180 ? lighter.hsl.H + 360 : lighter.hsl.H,
          lighter.hsl.H - darker.hsl.H > 180 ? darker.hsl.H + 360 : darker.hsl.H
        ) % 360;
      const luminosity = this._mapNumbers(index, lighter.index, darker.index, lighter.hsl.L, darker.hsl.L);
      const saturation = this._calculateSaturation(luminosity, lighter, darker, third);

      // Add shade
      const shade = new Shade(index, new Value({ H: hue, S: saturation, L: luminosity }), false);
      shades.push(shade);
    }

    // Filter "black" and "white" and sort again by index
    color.shades = shades
      .filter((shade) => !(shade.index === 0 || shade.index === 1200))
      .sort((a, b) => a.index - b.index);
  }

  /**
   * Distribute fixed indices from `[50, 100, 200, ..., 900]` to all shades by perceived brightness.
   * Every index will only appear once.
   * This is done by shifting a duplicate index to the next one.
   *
   * **Help wanted:** This algorithm could be improved by calculating penalty points for shifting, generating multiple variants and choosing the one with the fewest penalty points.
   * @param shades
   * @param indices
   */
  private _distributeIndices(shades: Array<Shade>, indices: Array<number>): void {
    shades.forEach((shade) => {
      const mapBrightness = Math.max(Math.min(1250 - 12.5 * shade.perceivedBrightness, 999), 1);
      const index = indices.reduce((prev, curr) =>
        Math.abs(mapBrightness - curr) < Math.abs(mapBrightness - prev) ? curr : prev
      );
      shade.index = index;
    });

    this._offsetIndicesFromLightest(shades);
  }

  /**
   * Offset indices from lightest to darkest so no index appears multiple times.
   * @param shades
   */
  private _offsetIndicesFromLightest(shades: Array<Shade>): void {
    let minIndex = 0;
    for (let i = 0; i < shades.length; i++) {
      const shade = shades[i];

      if (minIndex >= shade.index) {
        if (minIndex < 900) {
          shade.index = minIndex + 100 - (minIndex % 100);
        } else {
          shade.index = 900;
          this._offsetIndicesFromDarkest(shades, i);
        }
      }

      minIndex = shade.index;
    }
  }

  /**
   * Offset indices from darkest to lightest so the index of `shades[start]` is the biggest and no index appears multiple times.
   * @param shades
   * @param start
   */
  private _offsetIndicesFromDarkest(shades: Array<Shade>, start: number): void {
    let maxIndex = shades[start].index;
    for (let j = start - 1; j >= 0; j--) {
      const brighter = shades[j];

      if (maxIndex > brighter.index) return;

      brighter.index = maxIndex -= 100;
    }
  }

  /**
   * Calculate the lowest saturation using the current lowest offset with an adjusted one.
   *
   * Returns an object including the calculated saturation alongside the brightness of the shade with the lowest saturation.
   * @param shades
   */
  private _calculateMinSaturation(shades: Array<Shade>): {
    saturation: number;
    brightness: number;
  } {
    const shadeWithLowestSaturation = shades.reduce((prev, curr) => (curr.hsl.S < prev.hsl.S ? curr : prev));
    const differenceFromMiddleSquared = Math.pow(50 - shadeWithLowestSaturation.perceivedBrightness, 2);
    const evenLowerSaturation = Math.max(shadeWithLowestSaturation.hsl.S - 0.01 * differenceFromMiddleSquared, 0);
    return {
      saturation: Math.round((shadeWithLowestSaturation.hsl.S + evenLowerSaturation) / 2),
      brightness: shadeWithLowestSaturation.perceivedBrightness
    };
  }

  /**
   * Creates the most extreme shades ("black" and "white").
   * This is done with respect to the hue of the current lightest and darkest shades alongside the minimal saturation and brightness of the current color.
   * @param shade
   * @param minimum
   * @param white
   * @private
   */
  private _createBorderShade(shade: Shade, minimum: { saturation: number; brightness: number }, white: boolean): Shade {
    const index = white ? 0 : 1000;
    const hue = this._calculateHue(shade, white);
    const saturation = this._calculateMaxSaturation(minimum, shade, index);
    const luminosity = (1000 - index) / 10;

    return new Shade(white ? 0 : 1200, new Value({ H: hue, S: saturation, L: luminosity }), true);
  }

  /**
   * Calculate the hue of the lightest and darkest shades with the hue of the nearest neighbor in mind.
   * If we want to create a lighter shade, we rotate in direction 60, 180 and 300 degree respectively.
   * If we want to create a darker shade, we rotate in direction 0, 120 and 240 degree respectively.
   * @param shade
   * @param white
   */
  private _calculateHue(shade: Shade, white: boolean): number {
    const hueAdjustmentDirection =
      shade.hsl.H < 60 || (120 < shade.hsl.H && shade.hsl.H < 180) || (240 < shade.hsl.H && shade.hsl.H < 300) ? 1 : -1;

    let hue =
      shade.hsl.H + (hueAdjustmentDirection * (white ? 1 : -1) * (white ? shade.index : 1000 - shade.index)) / 60;

    if (white) {
      if ((shade.hsl.H <= 60 && hue > 60) || (shade.hsl.H >= 60 && hue < 60)) {
        hue = 60;
      } else if ((shade.hsl.H <= 180 && hue > 180) || (shade.hsl.H >= 180 && hue < 180)) {
        hue = 180;
      } else if ((shade.hsl.H <= 300 && hue > 300) || (shade.hsl.H >= 300 && hue < 300)) {
        hue = 300;
      }
    } else {
      if (
        (((shade.hsl.H > 340 && shade.hsl.H < 360) || shade.hsl.H === 0) && hue > 0 && hue < 20) ||
        (shade.hsl.H >= 0 && shade.hsl.H < 20 && hue < 0)
      ) {
        hue = 0;
      } else if ((shade.hsl.H <= 120 && hue > 120) || (shade.hsl.H >= 120 && hue < 120)) {
        hue = 120;
      } else if ((shade.hsl.H <= 240 && hue > 240) || (shade.hsl.H >= 240 && hue < 240)) {
        hue = 240;
      }
    }

    return Math.round(360 + hue) % 360;
  }

  /**
   * Calculate the highest saturation based on a neighbor with minimal saturation and the nearest neighbor.
   * @param minimum
   * @param neighbor
   * @param index
   */
  private _calculateMaxSaturation(
    minimum: { saturation: number; brightness: number },
    neighbor: Shade,
    index: number
  ): number {
    if (neighbor.perceivedBrightness === minimum.brightness)
      return Math.round(-0.01 * Math.pow(minimum.saturation, 2) + 2 * minimum.saturation);

    const a = (neighbor.hsl.S - minimum.saturation) / Math.pow(neighbor.perceivedBrightness - minimum.brightness, 2);
    const maxSaturation = a * Math.pow(index / 10 - minimum.brightness, 2) + minimum.saturation;

    const minAdd = -0.01 * Math.pow(neighbor.hsl.S, 2) + 2 * neighbor.hsl.S;

    return Math.min(Math.round(Math.max(maxSaturation, minAdd, 0)), 100);
  }

  /**
   * Map numbers on a linear 1 dimensional scale
   * @param x
   * @param in_min
   * @param in_max
   * @param out_min
   * @param out_max
   */
  private _mapNumbers(x: number, in_min: number, in_max: number, out_min: number, out_max: number): number {
    return Math.round(((x - in_min) * (out_max - out_min)) / (in_max - in_min) + out_min);
  }

  /**
   * Calculate the saturation by interpolating a value for the current luminosity on a quadratic 2 dimensional parable defined by three points (shades).
   * @param luminosity
   * @param left
   * @param middle
   * @param right
   */
  private _calculateSaturation(luminosity: number, left: Shade, middle: Shade, right: Shade): number {
    const ll2 = left.hsl.L * left.hsl.L;
    const ml2 = middle.hsl.L * middle.hsl.L;
    const rl2 = right.hsl.L * right.hsl.L;

    const delta =
      ll2 * middle.hsl.L +
        left.hsl.L * rl2 +
        ml2 * right.hsl.L -
        middle.hsl.L * rl2 -
        ll2 * right.hsl.L -
        left.hsl.L * ml2 || 0.01;
    const aNumerator =
      left.hsl.S * middle.hsl.L +
      left.hsl.L * right.hsl.S +
      middle.hsl.S * right.hsl.L -
      middle.hsl.L * right.hsl.S -
      left.hsl.S * right.hsl.L -
      left.hsl.L * middle.hsl.S;
    const bNumerator =
      ll2 * middle.hsl.S +
      left.hsl.S * rl2 +
      ml2 * right.hsl.S -
      middle.hsl.S * rl2 -
      ll2 * right.hsl.S -
      left.hsl.S * ml2;
    const cNumerator =
      ll2 * middle.hsl.L * right.hsl.S +
      left.hsl.L * middle.hsl.S * rl2 +
      left.hsl.S * ml2 * right.hsl.L -
      left.hsl.S * middle.hsl.L * rl2 -
      ll2 * middle.hsl.S * right.hsl.L -
      left.hsl.L * ml2 * right.hsl.S;

    const x = aNumerator / delta;
    const y = bNumerator / delta;
    const z = cNumerator / delta;

    const saturation = x * luminosity * luminosity + y * luminosity + z;
    return Math.max(Math.min(Math.round(saturation), 100), 0);
  }
}

