import { Formatter } from '../interfaces/formatter.interface';
import { Color } from '../model/color.model';
import { Palette } from '../model/palette.model';
import { Shade } from '../model/shade.model';

export class ScssFormatter implements Formatter {
  public filename = '_colors.scss';
  public mimeType = 'text/scss';

  public formatFile(palette: Palette): string {
    const content = this.formatPalette(palette);

    return `/* Import the variables into your SCSS files with\n\t@use 'colors';\n*/\n\n${content}`;
  }

  public formatPalette(palette: Palette): string {
    const content = palette.colors.map((color) => this.formatColor(color)).join('\n\n');

    return `/* Color palette generated by ${window.location.origin} */\n\n${content}`;
  }

  public formatColor(color: Color): string {
    const name = color.name.replace(/\s+/g, '-').toLowerCase();
    const shades = color.shades.map((shade) => this.formatShade(shade, name)).join('\n');

    return shades;
  }

  public formatShade(shade: Shade, name: string): string {
    return `$${name}-${shade.index}:${shade.index < 100 ? ' ' : ''} ${shade.hex};`;
  }
}

import { HSLObject } from '../types/color-format';
import { perceivedBrightnessFromRGB } from '../utils/perceived-brightness';
import { Value } from './value.model';

export class Shade {
  public index: number;
  public fixed: boolean;
  private _value: Value;
  private _perceivedBrightness?: number;

  public constructor(index: number, value: Value, fixed = false) {
    this.index = index;
    this._value = value;
    this.fixed = fixed;
  }

  public get hslValue(): string {
    const hsl = this._value.HSL;
    return `hsl(${hsl.H}, ${hsl.S}%, ${hsl.L}%)`;
  }

  public get hsl(): HSLObject {
    return this._value.HSL;
  }

  public get value(): HSLObject {
    return this._value.HSL;
  }

  public set hsl(hsl: HSLObject) {
    this._value.HSL = hsl;

    this._perceivedBrightness = undefined;
  }

  public get hex(): string {
    return this._value.HEX.toUpperCase();
  }

  public set hex(hex: string) {
    this._value.HEX = hex;

    this._perceivedBrightness = undefined;
  }

  public get perceivedBrightness(): number {
    if (this._perceivedBrightness !== undefined) {
      return this._perceivedBrightness;
    }

    this._perceivedBrightness = perceivedBrightnessFromRGB(this._value.RGB);
    return this._perceivedBrightness;
  }

  public copy(): Shade {
    return new Shade(this.index, new Value(this.hex), this.fixed);
  }

  public static random(): Shade {
    const h = Math.floor(Math.random() * 360);
    const s = 30 + Math.floor(Math.random() * 60);
    const l = 25 + Math.floor(Math.random() * 50);

    const value = new Value({ H: h, S: s, L: l });
    return new Shade(0, value, true);
  }

  public static parse(shade: string | object): Shade {
    if (typeof shade === 'string') {
      try {
        shade = JSON.parse(shade);
      } catch (e) {
        throw new Error(`Could not parse shade (not a valid JSON): "${shade}"`);
      }
    }

    if (!(shade instanceof Object)) {
      throw new Error(`Could not parse shade (not an object): "${shade}"`);
    }

    let index = 0;
    if ('index' in shade && typeof shade.index === 'number') {
      index = shade.index;
    }

    let fixed = false;
    if ('fixed' in shade && typeof shade.fixed === 'boolean') {
      fixed = shade.fixed;
    }

    if (!('value' in shade)) {
      if ('hex' in shade && typeof shade.hex === 'string') {
        return new Shade(index, new Value(shade.hex), fixed);
      }

      throw new Error(`Could not parse shade (missing "value" property): "${shade}"`);
    }

    let value: Value | undefined;
    if (typeof shade.value === 'string' && shade.value.match(/^#([A-Fa-f0-9]{6}|[A-Fa-f0-9]{3})$/)) {
      value = new Value(shade.value);
    } else if (typeof shade.value === 'object') {
      const hsl = shade.value as HSLObject;
      if (
        'H' in hsl &&
        'S' in hsl &&
        'L' in hsl &&
        typeof hsl.H === 'number' &&
        (typeof hsl.S === 'number' || typeof hsl.S === 'string') &&
        (typeof hsl.L === 'number' || typeof hsl.L === 'string')
      ) {
        value = new Value(hsl);
      }
    }

    if (value === undefined) {
      throw new Error(`Could not parse shade (invalid "value" property): "${shade.value}"`);
    }

    return new Shade(index, value, fixed);
  }

  public toJSON(): object {
    return {
      index: this.index,
      fixed: this.fixed,
      value: this._value.HSL
    };
  }

  public toString(): string {
    return JSON.stringify(this.toJSON());
  }
}

import { VersionEvent } from '@angular/service-worker';
import { Observable, Subject } from 'rxjs';
import { sleep } from './sleep';

const oldVersion = { hash: 'OLD', appData: { version: '0.0.0' } };
const newVersion = { hash: 'NEW', appData: { version: '1.0.0' } };

export class SwUpdateMock {
  private readonly _versionUpdates = new Subject<VersionEvent>();
  public readonly unrecoverable = new Subject<void>();

  public get versionUpdates(): Observable<VersionEvent> {
    return this._versionUpdates.asObservable();
  }

  public async emitNoUpdateAvailable(): Promise<void> {
    this._versionUpdates.next({
      type: 'NO_NEW_VERSION_DETECTED',
      version: oldVersion
    });

    await sleep(10);
  }

  public async emitUpdateAvailable(): Promise<void> {
    this._versionUpdates.next({
      type: 'VERSION_DETECTED',
      version: newVersion
    });

    await sleep(10);
  }

  public async emitInstallationFailed(): Promise<void> {
    this._versionUpdates.next({
      type: 'VERSION_INSTALLATION_FAILED',
      version: newVersion,
      error: 'Installation failed'
    });

    await sleep(10);
  }

  public async emitUpdateReady(): Promise<void> {
    this._versionUpdates.next({
      type: 'VERSION_READY',
      currentVersion: oldVersion,
      latestVersion: newVersion
    });

    await sleep(10);
  }
}

import { Formatter } from '../interfaces/formatter.interface';
import { Color } from '../model/color.model';
import { Palette } from '../model/palette.model';
import { Shade } from '../model/shade.model';

export class TailwindFormatter implements Formatter {
  public filename = 'tailwind.colors.js';
  public mimeType = 'text/javascript';

  public formatFile(palette: Palette): string {
    const content = this.formatPalette(palette).replace(/\n/g, '\n\t');

    return `module.exports = {\n\t${content}\n}`;
  }

  public formatPalette(palette: Palette): string {
    return palette.colors.map((color) => this.formatColor(color)).join(',\n');
  }

  public formatColor(color: Color): string {
    const name = color.name.replace(/\s+/g, '-').toLowerCase();
    const shades = color.shades.map((shade) => this.formatShade(shade)).join(',\n');

    return `'${name}': {\n${shades}\n}`;
  }

  public formatShade(shade: Shade): string {
    return `\t${shade.index}:${shade.index < 100 ? ' ' : ''} '${shade.hex}'`;
  }
}

import { Injectable, Signal, effect, signal } from '@angular/core';
import { LocalStorageKey } from '../enums/local-storage-keys';
import { Theme } from '../types/theme';

@Injectable({
  providedIn: 'root'
})
export class ThemeService {
  private readonly _theme = signal<Theme>('light');

  public get theme(): Signal<Theme> {
    return this._theme.asReadonly();
  }

  public constructor() {
    effect(async () => {
      localStorage.setItem(LocalStorageKey.THEME, this._theme());
      document.documentElement.classList.toggle('dark', this._theme() === 'dark');
    });

    const storedTheme = localStorage.getItem(LocalStorageKey.THEME);
    if (storedTheme === 'dark') {
      this._theme.set('dark');
      return;
    } else if (storedTheme === 'light') {
      return;
    }

    const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
    if (prefersDark) {
      this._theme.set('dark');
    }
  }

  public setTheme(theme: Theme): void {
    this._theme.set(theme);
  }
}

export class ThemeServiceMock {
  public readonly theme = signal<Theme>('light').asReadonly();
  public setTheme(_theme: Theme): void {}
}

import { GlobalPositionStrategy, Overlay, OverlayRef, PositionStrategy } from '@angular/cdk/overlay';
import { ComponentPortal } from '@angular/cdk/portal';
import { Injectable, Signal, effect, inject, signal } from '@angular/core';
import { Toast, ToastTimeouts } from '../interfaces/toast.interface';
import { ToastComponent } from '../ui/toast/toast.component';
import { MobileService } from './mobile.service';

@Injectable({
  providedIn: 'root'
})
export class ToastService {
  private readonly _overlay = inject(Overlay);
  private readonly _mobileService = inject(MobileService);

  private readonly _toast = signal<Toast | undefined>(undefined);

  public get toast(): Signal<Toast | undefined> {
    return this._toast.asReadonly();
  }

  private _hideToast: ReturnType<typeof setTimeout> | undefined;
  private _positionStrategy: PositionStrategy | undefined;
  private _overlayRef: OverlayRef | undefined;

  public constructor() {
    // Create an overlay for the toast.
    this._overlayRef = this._overlay.create();

    effect(() => {
      // If there is no toast, clear the timeout and return.
      if (!this._toast()) {
        if (this._hideToast) {
          clearTimeout(this._hideToast);
        }
        this._hideToast = undefined;
        return;
      }

      // If there was a previous timeout, clear it.
      if (this._hideToast) {
        clearTimeout(this._hideToast);
        this._hideToast = undefined;
      }

      // Set a new timeout to hide the toast.
      this._hideToast = setTimeout(
        () => {
          this._toast.set(undefined);
        },
        ToastTimeouts[this._toast()!.type ?? 'default']
      );
    });

    // Attach the toast component to the overlay.
    effect(() => {
      if (this._toast()) {
        if (!this._overlayRef) {
          return;
        }

        this._overlayRef.detach();
        const toastPortal = new ComponentPortal(ToastComponent);
        const toastComponent = this._overlayRef.attach(toastPortal);
        toastComponent.setInput('toast', this._toast());
        const closeSubscription = toastComponent.instance.close.subscribe(() => {
          this.hideToast();
          closeSubscription.unsubscribe();
        });
      } else {
        this._overlayRef?.detach();
      }
    });

    // Update the position strategy based on the device type.
    effect(() => {
      if (this._mobileService.isMobile()) {
        this._positionStrategy = new GlobalPositionStrategy().top('0.5rem').centerHorizontally();
      } else {
        this._positionStrategy = new GlobalPositionStrategy().bottom('0.5rem').end('0.5rem');
      }

      this._overlayRef?.updatePositionStrategy(this._positionStrategy);
    });
  }

  /**
   * Shows the toast.
   *
   * @param toast The toast to be displayed.
   */
  public showToast(toast: Toast): void {
    this._toast.set(toast);
  }

  /**
   * Hides the toast.
   */
  public hideToast(): void {
    this._toast.set(undefined);
  }
}

export class ToastServiceMock {
  private readonly _toast = signal<Toast | undefined>({
    type: 'test',
    message: 'test'
  });

  public get toast(): Signal<Toast | undefined> {
    return this._toast.asReadonly();
  }

  public showToast(toast: Toast): void {
    this._toast.set(toast);
  }

  public hideToast(): void {
    this._toast.set(undefined);
  }
}

import { HSLObject, RGBObject } from '../types/color-format';

export class Value {
  private _hue!: number;
  private _saturation!: number;
  private _lightness!: number;
  private _red!: number;
  private _green!: number;
  private _blue!: number;

  public constructor(value: HSLObject | RGBObject | string) {
    if (typeof value === 'string') {
      this.HEX = value;
    } else if ('R' in value) {
      this.RGB = value;
    } else {
      this.HSL = value;
    }
  }

  public set HSL(value: HSLObject) {
    if (value.H < 0 || value.H > 360 || value.S < 0 || value.S > 100 || value.L < 0 || value.L > 100) {
      throw `Color values [${value.H}, ${value.S}%, ${value.L}%] are not in valid ranges.`;
    }

    this._hue = value.H;
    this._saturation = value.S;
    this._lightness = value.L;

    const rgb = this._HSLtoRGB(this._hue, this._saturation, this._lightness);
    this._red = rgb.R;
    this._green = rgb.G;
    this._blue = rgb.B;
  }

  public get HSL(): HSLObject {
    return {
      H: this._hue,
      S: this._saturation,
      L: this._lightness
    };
  }

  public set RGB(value: RGBObject) {
    if (value.R < 0 || value.R > 255 || value.G < 0 || value.G > 255 || value.B < 0 || value.B > 255) {
      throw `rgb(${value.R}, ${value.G}, ${value.B}) is not in valid format.`;
    }

    this._red = value.R;
    this._green = value.G;
    this._blue = value.B;

    const hsl = this._RGBtoHSL(this._red, this._green, this._blue);
    this._hue = hsl.H;
    this._saturation = hsl.S;
    this._lightness = hsl.L;
  }

  public get RGB(): RGBObject {
    return {
      R: this._red,
      G: this._green,
      B: this._blue
    };
  }

  public set HEX(hex: string) {
    if (!hex.match(/^#([A-Fa-f0-9]{6}|[A-Fa-f0-9]{3})$/)) {
      throw new Error(`Invalid hex color: "${hex}`);
    }

    const rgb = {
      R: parseInt(hex.substring(1, 3), 16),
      G: parseInt(hex.substring(3, 5), 16),
      B: parseInt(hex.substring(5, 7), 16)
    };

    this.RGB = rgb;
  }

  public get HEX(): string {
    const red = this._red.toString(16).padStart(2, '0');
    const green = this._green.toString(16).padStart(2, '0');
    const blue = this._blue.toString(16).padStart(2, '0');

    return `#${red}${green}${blue}`;
  }

  private _RGBtoHSL(red: number, green: number, blue: number): HSLObject {
    const r = red / 255;
    const g = green / 255;
    const b = blue / 255;

    const cMax = Math.max(r, g, b);
    const cMin = Math.min(r, g, b);
    const delta = cMax - cMin;

    const lightness = (cMax + cMin) * 50;
    const saturation = delta === 0 ? 0 : (100 * delta) / (1 - Math.abs(2 * (lightness / 100) - 1));

    let hue;
    if (delta === 0) hue = 0;
    else if (cMax === r) hue = 60 * (((g - b) / delta) % 6);
    else if (cMax === g) hue = 60 * ((b - r) / delta + 2);
    else hue = 60 * ((r - g) / delta + 4);

    if (hue < 0) hue += 360;

    return {
      H: Math.round(hue),
      S: Math.round(saturation),
      L: Math.round(lightness)
    };
  }

  private _HSLtoRGB(hue: number, saturation: number, lightness: number): RGBObject {
    const h = hue;
    const s = saturation / 100;
    const l = lightness / 100;

    const c = (1 - Math.abs(2 * l - 1)) * s;
    const x = c * (1 - Math.abs(((h / 60) % 2) - 1));
    const m = l - c / 2;

    const r = h < 60 || h >= 300 ? c : h < 120 || h >= 240 ? x : 0;
    const g = h >= 240 ? 0 : h < 60 || h >= 180 ? x : c;
    const b = h < 120 ? 0 : h < 180 || h >= 300 ? x : c;

    return {
      R: Math.round((r + m) * 255),
      G: Math.round((g + m) * 255),
      B: Math.round((b + m) * 255)
    };
  }
}

