import { Component, ElementRef, computed, effect, input, model, output, viewChild } from '@angular/core';
import { NgIconComponent } from '@ng-icons/core';
import { heroEyeDropperMini } from '@ng-icons/heroicons/mini';
import { TranslateModule } from '@ngx-translate/core';
import { perceivedBrightnessFromHex } from '../../utils/perceived-brightness';

@Component({
  selector: 'rp-color-input',
  standalone: true,
  imports: [TranslateModule, NgIconComponent],
  templateUrl: './color-input.component.html'
})
export class ColorInputComponent {
  protected readonly heroEyeDropperMini = heroEyeDropperMini;

  /**
   * Placeholder to display in the input field.
   */
  public readonly placeholder = input.required<string>();
  /**
   * Tooltip to display on the eye dropper icon.
   */
  public readonly tooltip = input.required<string>();
  /**
   * Hex color value.
   */
  public readonly hex = model.required<string>();
  /**
   * Whether the hex color value is valid.
   */
  public readonly isValid = output<boolean>();

  /**
   * Reference to the hex input element.
   */
  private readonly _hexInput = viewChild.required<ElementRef<HTMLInputElement>>('hexInput');

  /**
   * Whether the color is light.
   */
  protected readonly isColorLight = computed(() => {
    return perceivedBrightnessFromHex(this.hex()) > 51;
  });

  public constructor() {
    effect(() => {
      this._hexInput().nativeElement.value = this.hex();
    });
  }

  /**
   * Update the hex color value.
   *
   * @param hex Value to update the hex color to.
   */
  protected updateHex(hex: string): void {
    // Remove leading / trailing whitespace and non-hex characters
    hex = hex.trim().replace(/[^0-9a-fA-F]/g, '');

    // Add leading hash if missing
    if (!hex.startsWith('#')) {
      hex = `#${hex}`;
    }

    // Normalize to uppercase and remove extra characters
    hex = hex.toUpperCase().slice(0, 7);

    // Set color if it is a valid hex color
    if (hex.length === 4 || hex.length === 7) {
      this.hex.set(hex);
      this.isValid.emit(true);
    } else {
      this.isValid.emit(false);
    }

    // Update input value
    this._hexInput().nativeElement.value = hex;
  }
}

import { CdkMenu, CdkMenuGroup, CdkMenuItemRadio, CdkMenuTrigger } from '@angular/cdk/menu';
import { ConnectedPosition } from '@angular/cdk/overlay';
import { CommonModule } from '@angular/common';
import {
  Component,
  ElementRef,
  TemplateRef,
  booleanAttribute,
  contentChild,
  effect,
  input,
  model,
  signal,
  viewChild
} from '@angular/core';
import { TranslateModule } from '@ngx-translate/core';
import { Subscription } from 'rxjs';

@Component({
  selector: 'rp-dropdown-menu',
  standalone: true,
  imports: [CdkMenuTrigger, CdkMenu, CdkMenuGroup, CdkMenuItemRadio, TranslateModule, CommonModule],
  templateUrl: './dropdown-menu.component.html'
})
export class DropdownMenuComponent<T> {
  /**
   * The items that are displayed in the dropdown menu.
   */
  public readonly items = input.required<Array<T>>();
  /**
   * Title on top of the dropdown menu.
   */
  public readonly title = input<string | undefined>();
  /**
   * If the dropdown menu is disabled it will not open when the user clicks on the trigger.
   */
  public readonly disabled = input(false, {
    transform: booleanAttribute
  });
  /**
   * Whether the dropdown menu should close when the user scrolls outside of the menu.
   */
  public readonly closeOnScroll = input(true, {
    transform: booleanAttribute
  });
  /**
   * Whether the selected item should be highlighted in the dropdown menu.
   */
  public readonly highlightSelection = input(false, {
    transform: booleanAttribute
  });
  /**
   * Minimum width of the dropdown menu.
   */
  public readonly minWidth = input('12rem');
  /**
   * Maximum width of the dropdown menu.
   */
  public readonly maxWidth = input('40rem');
  /**
   * Minimum height of the dropdown menu.
   * This does not include the height of the title but only the menu items.
   */
  public readonly minHeight = input<string | undefined>();
  /**
   * Maximum height of the dropdown menu.
   * This does not include the height of the title but only the menu items.
   * If the height of the menu items exceeds this value, the menu will be scrollable.
   */
  public readonly maxHeight = input('16rem');

  /**
   * The currently selected item in the dropdown menu.
   * This can be pre-selected by setting this input to the value of the item.
   * This will also be updated when the user selects an item in the dropdown menu.
   */
  public readonly selectedItem = model<T | undefined>(undefined);

  /**
   * The template that is used to render the items in the dropdown menu.
   * You can use this to customize the appearance of the items.
   * The template context contains the item that is currently being rendered.
   */
  public readonly itemTemplate = contentChild<TemplateRef<{ item: T }>>('itemTemplate');

  // View Child Signals
  private readonly _trigger = viewChild(CdkMenuTrigger);
  private readonly _menu = viewChild<ElementRef<HTMLElement>>('menuGroup');

  // Internal Signals and Properties
  private readonly _isOpen = signal(false);
  private readonly _abortControllers: Array<AbortController> = [];
  private _openedSubscription?: Subscription;
  private _closedSubscription?: Subscription;

  protected readonly menuPositions: Array<ConnectedPosition> = [
    {
      originX: 'end',
      originY: 'bottom',
      overlayX: 'end',
      overlayY: 'top',
      offsetY: 8
    },
    {
      originX: 'start',
      originY: 'bottom',
      overlayX: 'start',
      overlayY: 'top',
      offsetY: 8
    },
    {
      originX: 'end',
      originY: 'top',
      overlayX: 'end',
      overlayY: 'bottom',
      offsetY: -8
    },
    {
      originX: 'start',
      originY: 'top',
      overlayX: 'start',
      overlayY: 'bottom',
      offsetY: -8
    }
  ];

  public constructor() {
    /**
     * Effect to handle the opened and closed subscriptions of the trigger.
     * These subscriptions are used to update the isOpen signal when the
     * menu is opened or closed.
     */
    effect(() => {
      if (this._openedSubscription) {
        this._openedSubscription.unsubscribe();
      }
      if (this._closedSubscription) {
        this._closedSubscription.unsubscribe();
      }

      if (this._trigger()) {
        this._openedSubscription = this._trigger()!.opened.subscribe(() => {
          this._isOpen.set(true);
        });
        this._closedSubscription = this._trigger()!.closed.subscribe(() => {
          this._isOpen.set(false);
        });
      }
    });

    /**
     * Effect to handle closing the menu when the user scrolls outside of the menu.
     * This effect will add an event listener to the window scroll event when the
     * menu is open (and {@link closeOnScroll} is true).
     * These event listeners are also automatically removed when the menu is closed again.
     */
    effect(() => {
      if (this._isOpen()) {
        if (!this.closeOnScroll()) {
          return;
        }

        const abortController = new AbortController();
        this._abortControllers.push(abortController);

        window.addEventListener(
          'scroll',
          (event: Event) => {
            if (event.target === this._menu()?.nativeElement) {
              return;
            }

            this._trigger()?.close();
          },
          {
            capture: true,
            signal: abortController.signal
          }
        );
      } else {
        while (this._abortControllers.length > 0) {
          this._abortControllers.shift()?.abort();
        }
      }
    });
  }

  protected select(item: T): void {
    this.selectedItem.set(item);
  }
}

import { DIALOG_DATA, DialogRef } from '@angular/cdk/dialog';
import { DecimalPipe } from '@angular/common';
import { Component, ElementRef, computed, effect, inject, signal, viewChild } from '@angular/core';
import { TranslateModule, TranslateService } from '@ngx-translate/core';
import { ColorService } from '../shared/data-access/color.service';
import { Color, Shade } from '../shared/model';
import { ColorInputComponent } from '../shared/ui/color-input/color-input.component';
import { textColor } from '../shared/utils/text-color';
import { EditorRangeComponent } from './ui/editor-range/editor-range.component';

export enum UpdateType {
  HEX = 'hex',
  HUE = 'hue',
  SATURATION = 'saturation',
  LIGHTNESS = 'lightness'
}

@Component({
  selector: 'rp-editor',
  standalone: true,
  imports: [ColorInputComponent, TranslateModule, DecimalPipe, EditorRangeComponent],
  templateUrl: './editor.component.html'
})
export class EditorComponent {
  protected readonly UpdateType = UpdateType;
  protected readonly textColor = textColor;

  private readonly _data = inject<{ color: Color; shadeIndex?: number }>(DIALOG_DATA);
  private readonly _dialogRef = inject(DialogRef);
  private readonly _colorService = inject(ColorService);
  private readonly _translateService = inject(TranslateService);

  protected readonly color = signal(this._data.color.copy());
  protected readonly shadeIndex = signal(this._data.shadeIndex ?? 0);

  protected readonly shade = computed<Shade>(() => {
    const selectedShade = this.color().shades.find((shade) => shade.index === this.shadeIndex());
    if (selectedShade) {
      return selectedShade;
    }

    const fixedShade = this.color().shades.find((shade) => shade.fixed);
    if (fixedShade) {
      return fixedShade;
    }

    return this.color().shades[0];
  });

  private readonly _editor = viewChild.required<ElementRef<HTMLElement>>('editor');

  public constructor() {
    effect(() => {
      this._editor().nativeElement.style.setProperty('--editor-hue', `${this.shade().hsl.H}`);
      this._editor().nativeElement.style.setProperty('--editor-saturation', `${this.shade().hsl.S}%`);
      this._editor().nativeElement.style.setProperty('--editor-lightness', `${this.shade().hsl.L}%`);
    });
  }

  protected changeShade(index: number): void {
    this.shadeIndex.set(index);
  }

  public unfixShade(shade: Shade, $event?: MouseEvent): void {
    if ($event) {
      $event.stopPropagation();
      $event.preventDefault();
    }

    if (!shade.fixed) {
      return;
    }

    const atLeastOneFixedLeft = this.color().shades.some((s) => s.fixed && s !== shade);

    if (!atLeastOneFixedLeft) {
      return;
    }

    shade.fixed = false;
    this._updateColor();
  }

  public update(type: UpdateType, value: string | number): void {
    const shade = this.shade();
    shade.fixed = true;

    switch (type) {
      case UpdateType.HEX:
        shade.hex = value as string;
        break;
      case UpdateType.HUE:
        shade.hsl = { ...shade.hsl, H: value as number };
        break;
      case UpdateType.SATURATION:
        shade.hsl = { ...shade.hsl, S: value as number };
        break;
      case UpdateType.LIGHTNESS:
        shade.hsl = { ...shade.hsl, L: value as number };
        break;
    }

    this._updateColor();

    const editedShade = this.color().shades.find((s) => s.hex === shade.hex);
    this.shadeIndex.set(editedShade?.index ?? -1);
  }

  protected cancel(): void {
    this._dialogRef.close(undefined);
    this.color.set(this._data.color.copy());
  }

  protected save(): void {
    this._dialogRef.close(this.color());
  }

  private _updateColor(): void {
    const updatedColor = this.color().copy();
    this._colorService.regenerateShades(updatedColor);
    this.color.set(updatedColor);
  }

  protected getTooltip(shade: Shade, selected: boolean): string {
    const tooltips: Array<string> = [];

    if (!selected) {
      tooltips.push(this._translateService.instant('editor.shades'));
    }

    if (shade.fixed) {
      tooltips.push(this._translateService.instant('editor.unfix'));
    }

    return tooltips.join('\n');
  }
}

import { DecimalPipe } from '@angular/common';
import { Component, computed, input, model, numberAttribute } from '@angular/core';
import { hueToWheel, wheelToHue } from '../../utils/color-wheel';

@Component({
  selector: 'rp-editor-range',
  standalone: true,
  imports: [DecimalPipe],
  templateUrl: './editor-range.component.html',
  styleUrl: './editor-range.component.css'
})
export class EditorRangeComponent {
  /**
   * Label to display above the range input
   */
  public readonly label = input.required<string>();
  /**
   * Tooltip to display when hovering over the range input
   */
  public readonly tooltip = input.required<string>();
  /**
   * Key to identify the type of value being adjusted
   */
  public readonly key = input.required<'hue' | 'saturation' | 'lightness'>();
  /**
   * Current value of the range input
   */
  public readonly value = model.required<number>();
  /**
   * Minimum value of the range input
   */
  public readonly min = input(0, {
    transform: numberAttribute
  });
  /**
   * Maximum value of the range input
   */
  public readonly max = input(100, {
    transform: numberAttribute
  });

  protected readonly transformedValue = computed(() => {
    if (this.key() === 'hue') {
      return hueToWheel(this.value());
    } else if (this.key() === 'lightness') {
      return 100 - this.value();
    } else {
      return this.value();
    }
  });

  protected updateValue(value: number | string): void {
    if (typeof value === 'string') {
      value = parseFloat(value);
    }

    if (this.key() === 'hue') {
      this.value.set(wheelToHue(value));
    } else if (this.key() === 'lightness') {
      this.value.set(100 - value);
    } else {
      this.value.set(value);
    }
  }
}

