import { DIALOG_DATA, DialogRef } from '@angular/cdk/dialog';
import { Component, inject, signal } from '@angular/core';
import { NgIconComponent } from '@ng-icons/core';
import { heroArrowLeftMini, heroXMarkMini } from '@ng-icons/heroicons/mini';
import { TranslateModule } from '@ngx-translate/core';
import { ExportFormat } from '../shared/constants/export-format';
import { AnalyticsService } from '../shared/data-access/analytics.service';
import { ExportService } from '../shared/data-access/export.service';
import { ToastService } from '../shared/data-access/toast.service';
import { TrackingEventAction, TrackingEventCategory } from '../shared/enums/tracking-event';
import { Palette } from '../shared/model';
import { ExportOption } from '../shared/types/export-option';
import { ExportDownloadComponent } from './ui/export-download/export-download.component';
import { ExportFormatComponent } from './ui/export-format/export-format.component';
import { ExportSuccessComponent } from './ui/export-success/export-success.component';
import { RequestFormatComponent } from './ui/request-format/request-format.component';

enum ExportModalState {
  FORMAT = 'format',
  DOWNLOAD = 'download',
  SUCCESS = 'success'
}

@Component({
  selector: 'rp-export-modal',
  standalone: true,
  imports: [
    TranslateModule,
    ExportFormatComponent,
    ExportDownloadComponent,
    ExportSuccessComponent,
    RequestFormatComponent,
    NgIconComponent
  ],
  templateUrl: './export-modal.component.html'
})
export class ExportModalComponent {
  protected readonly ExportModalState = ExportModalState;
  protected readonly ExportFormat = ExportFormat;

  private readonly _data = inject<{ palette: Palette }>(DIALOG_DATA);
  private readonly _dialogRef = inject(DialogRef);
  private readonly _toastService = inject(ToastService);
  private readonly _exportService = inject(ExportService);
  private readonly _analyticsService = inject(AnalyticsService);

  protected readonly palette = signal(this._data.palette);
  protected readonly state = signal(ExportModalState.FORMAT);
  protected readonly format = signal<ExportFormat | undefined>(undefined);
  protected readonly downloadOption = signal<ExportOption | undefined>(undefined);

  protected readonly heroXMarkMini = heroXMarkMini;
  protected readonly heroArrowLeftMini = heroArrowLeftMini;

  protected choseFormat(format: ExportFormat): void {
    this.format.set(format);
    this.state.set(ExportModalState.DOWNLOAD);

    if (format === ExportFormat.OTHER) {
      this._analyticsService.trackEvent(
        TrackingEventCategory.EXPORT_PALETTE,
        TrackingEventAction.EXPORT_PALETTE_REQUEST_FORMAT
      );
    }
  }

  protected async choseDownloadFormat(action: ExportOption): Promise<void> {
    this.downloadOption.set(action);

    const format = this.format();
    if (!format) {
      this._toastService.showToast({
        type: 'error',
        message: 'export.error.format-not-found'
      });
      this.close();
      return;
    }

    const success = await this._exportService.exportPalette(this.palette(), format, action);

    if (success) {
      this.state.set(ExportModalState.SUCCESS);
    } else {
      this.close();
    }
  }

  protected back(): void {
    switch (this.state()) {
      case ExportModalState.DOWNLOAD:
        this.state.set(ExportModalState.FORMAT);
        break;
      case ExportModalState.SUCCESS:
        this.state.set(ExportModalState.DOWNLOAD);
        break;
    }
  }

  protected close(): void {
    this._dialogRef.close();
  }
}

import { Component, computed, input, output } from '@angular/core';
import { NgIconComponent } from '@ng-icons/core';
import { TranslateModule } from '@ngx-translate/core';
import { string_to_unicode_variant as toUnicodeVariant } from 'string-to-unicode-variant';
import { ExportFormat } from '../../../shared/constants/export-format';
import { ExportOption } from '../../../shared/types/export-option';

@Component({
  selector: 'rp-export-success',
  standalone: true,
  imports: [TranslateModule, NgIconComponent],
  templateUrl: './export-success.component.html',
  styles: `
    :host {
      display: block;
    }
  `
})
export class ExportSuccessComponent {
  public readonly exportFormat = input.required<ExportFormat>();
  public readonly exportOption = input.required<ExportOption>();

  public readonly openDocumentation = output<void>();

  protected readonly description = computed(() => {
    return `export.success.description.${this.exportFormat()}.${this.exportOption()}`;
  });

  protected readonly descriptionParams = computed<{
    config?: string;
    docs: string;
    file?: string;
    link?: string;
    root?: string;
    usage?: string;
  }>(() => {
    const option = this.exportOption();

    if (option === 'copy') {
      switch (this.exportFormat()) {
        case ExportFormat.CSS:
          return {
            docs: 'https://developer.mozilla.org/en-US/docs/Web/CSS/Using_CSS_custom_properties#using_the_root_pseudo-class',
            root: toUnicodeVariant(':root', 'm'),
            usage: toUnicodeVariant('color: var(--color-100);', 'm')
          };
        case ExportFormat.SCSS:
          return {
            docs: 'https://sass-lang.com/documentation/variables',
            usage: toUnicodeVariant('color: $color-500;', 'm')
          };
        case ExportFormat.LESS:
          return {
            docs: 'https://lesscss.org/features/#variables-feature-overview',
            usage: toUnicodeVariant('color: @color-500;', 'm')
          };
        case ExportFormat.TAILWIND:
          return {
            config: toUnicodeVariant('tailwind.config.js', 'm'),
            docs: 'https://tailwindcss.com/docs/customizing-colors#color-object-syntax'
          };
        default:
          return { docs: '' };
      }
    } else {
      switch (this.exportFormat()) {
        case ExportFormat.CSS:
          return {
            docs: 'https://developer.mozilla.org/en-US/docs/Web/CSS/Using_CSS_custom_properties#using_the_root_pseudo-class',
            file: toUnicodeVariant('colors.css', 'm'),
            link: toUnicodeVariant('<link rel="stylesheet" href="/colors.css">', 'm'),
            usage: toUnicodeVariant('color: var(--color-100);', 'm')
          };
        case ExportFormat.SCSS:
          return {
            docs: 'https://sass-lang.com/documentation/at-rules/use',
            file: toUnicodeVariant('_colors.scss', 'm'),
            link: toUnicodeVariant("@use 'colors'", 'm'),
            usage: toUnicodeVariant('color: $color-500;', 'm')
          };
        case ExportFormat.LESS:
          return {
            docs: 'https://lesscss.org/features/#import-atrules-feature',
            file: toUnicodeVariant('colors.less', 'm'),
            link: toUnicodeVariant("@import 'colors.less'", 'm'),
            usage: toUnicodeVariant('color: @color-500;', 'm')
          };
        case ExportFormat.TAILWIND:
          return {
            config: toUnicodeVariant('tailwind.config.js', 'm'),
            docs: 'https://tailwindcss.com/docs/customizing-colors#using-the-default-colors',
            file: toUnicodeVariant('tailwind.colors.js', 'm'),
            import: toUnicodeVariant("colors: require('./tailwind.colors'),", 'm')
          };
        default:
          return { docs: '' };
      }
    }
  });

  protected openDocs(): void {
    this.openDocumentation.emit();
  }
}

