import {ColorInterpolater} from "../class/color-interpolater";
import {Shade} from "./shade.model";

export class Color {

  name: string

  shades: Shade[]

  public constructor(name: string, hex: string)
  public constructor(name: string, hexes: string[])
  public constructor(name: string, shades: Shade[])

  constructor(...args: any[]) {
    this.name = args[0].startsWith('#') ? args[0].substring(1) : args[0]

    if (typeof args[1] === 'string') {
      this.shades = []
      const shade = new Shade(-1, true, args[1])
      this.shades.push(shade)
    } else if (typeof args[1][0] === 'string') {
      this.shades = []

      for (const hex of args[1]) {
        this.shades.push(new Shade(-1, true, hex))
      }
    } else {
      this.shades = args[1]
    }

    ColorInterpolater.regenerateShades(this)
  }

  /**
   * Get shade by index (50, 100, 200, ..., 900)
   * @param index
   */
  public getShade(index: number): Shade {
    return this.shades.find(s => s.index === index) || this.shades[0]
  }

  /**
   * Generate a random color with all shades.
   */
  public static generateRandomColor(): Color {
    const shade = Shade.generateRandomShade()
    return new Color(
      shade.hex,
      shade.hex
    )
  }

  /**
   * Stringify the color.
   */
  public toString() {
    return JSON.stringify({
      name: this.name,
      shades: this.shades
    })
  }

  /**
   * Parse the json object to a color.
   * Throws exception if json object is no valid color object.
   * @param json
   */
  public static parseColor(json: any): Color {
    if (!json.name)
      throw 'Color has no name'
    if (!json.shades || json.shades.length < 1)
      throw 'Color has not enough shades'

    const shades: Shade[] = []
    for (const shade of json.shades) {
      shades.push(Shade.parseShade(shade))
    }

    return new Color(json.name, shades)
  }

}

export class ColorConverter {

  static HEXtoRGB(hex: string) {
    if (!hex.match(/^#[0-9A-Fa-f]{6}$/))
      throw `Color ${hex} is not in form #RRGGBB.`

    return {
      red: parseInt(hex.substring(1, 3), 16),
      green: parseInt(hex.substring(3, 5), 16),
      blue: parseInt(hex.substring(5, 7), 16)
    }
  }

  static HEXtoHSL(hex: string) {
    const rgb = this.HEXtoRGB(hex)
    return this.RGBtoHSL(rgb.red, rgb.green, rgb.blue)
  }

  static HEXtoHSV(hex: string) {
    const hsl = this.HEXtoHSL(hex)
    return this.HSLtoHSV(hsl.hue, hsl.saturation, hsl.luminosity)
  }

  static HEXtoCMYK(hex: string) {
    const rgb = this.HEXtoRGB(hex)
    return this.RGBtoCMYK(rgb.red, rgb.green, rgb.blue)
  }


  static RGBtoHEX(red: number, green: number, blue: number) {
    if (red < 0 || red > 255 || green < 0 || green > 255 || blue < 0 || blue > 255)
      throw `rgb(${red}, ${green}, ${blue}) is not in valid format.`

    return `#${
      (red < 16 ? 0 : '') + red.toString(16).toUpperCase()
    }${
      (green < 16 ? 0 : '') + green.toString(16).toUpperCase()
    }${
      (blue < 16 ? 0 : '') + blue.toString(16).toUpperCase()
    }`
  }

  static RGBtoHSL(red: number, green: number, blue: number) {
    if (red < 0 || red > 255 || green < 0 || green > 255 || blue < 0 || blue > 255)
      throw `rgb(${red}, ${green}, ${blue}) is not in valid format.`

    const r = red / 255
    const g = green / 255
    const b = blue / 255

    const cMax = Math.max(r, g, b)
    const cMin = Math.min(r, g, b)
    const delta = cMax - cMin

    const luminosity = (cMax + cMin) * 50
    const saturation = (delta === 0) ? 0 : (100 * delta / (1 - Math.abs(2 * (luminosity / 100) - 1)))

    let hue
    if (delta === 0)
      hue = 0
    else if (cMax === r)
      hue = 60 * ( ( (g - b) / delta) % 6)
    else if (cMax === g)
      hue = 60 * ( ( (b - r) / delta) + 2)
    else
      hue = 60 * ( ( (r - g) / delta) + 4)

    if (hue < 0)
      hue += 360

    return {
      hue: Math.round(hue),
      saturation: Math.round(saturation),
      luminosity: Math.round(luminosity)
    }
  }

  static RGBtoHSV(red: number, green: number, blue: number) {
    const hsl = this.RGBtoHSL(red, green, blue)
    return this.HSLtoHSV(hsl.hue, hsl.saturation, hsl.luminosity)
  }

  static RGBtoCMYK(red: number, green: number, blue: number) {
    if (red < 0 || red > 255 || green < 0 || green > 255 || blue < 0 || blue > 255)
      throw `rgb(${red}, ${green}, ${blue}) is not in valid format.`

    const r = red / 255
    const g = green / 255
    const b = blue / 255

    const k = 1 - Math.max(r, g, b)
    const c = (1 - r - k) / (1 - k)
    const m = (1 - g - k) / (1 - k)
    const y = (1 - b - k) / (1 - k)

    return {
      cyan: Math.round(c * 100),
      magenta: Math.round(m * 100),
      yellow: Math.round(y * 100),
      key: Math.round(k * 100)
    }
  }


  static HSLtoHEX(hue: number, saturation: number, luminosity: number): string {
    const rgb = this.HSLtoRGB(hue, saturation, luminosity)
    return this.RGBtoHEX(rgb.red, rgb.green, rgb.blue)
  }

  static HSLtoRGB(hue: number, saturation: number, luminosity: number) {
    if (hue < 0 || hue > 360 || saturation < 0 || saturation > 100 || luminosity < 0 || luminosity > 100)
      throw `Color values [${hue}, ${saturation}%, ${luminosity}%] are not in valid ranges.`

    const h = hue
    const s = saturation / 100
    const l = luminosity / 100

    const c = (1 - Math.abs(2 * l - 1)) * s
    const x = c * (1 - Math.abs((h / 60) % 2 - 1))
    const m = l - c / 2

    const r = (h < 60 || h >= 300) ? c : (h < 120 || h >= 240) ? x : 0
    const g = (h >= 240) ? 0 : (h < 60 || h >= 180) ? x : c
    const b = (h < 120) ? 0 : (h < 180 || h >= 300) ? x : c

    return {
      red: Math.round((r + m) * 255),
      green: Math.round((g + m) * 255),
      blue: Math.round((b + m) * 255)
    }
  }

  static HSLtoHSV(hue: number, saturation: number, luminosity: number) {
    if (hue < 0 || hue > 360 || saturation < 0 || saturation > 100 || luminosity < 0 || luminosity > 100)
      throw `Color values [${hue}, ${saturation}%, ${luminosity}%] are not in valid ranges.`

    const v = luminosity + saturation * Math.min(luminosity, 1 - luminosity)

    const s = (v === 0) ? 0 :
      (2 * (1 - (luminosity / v)))

    return {
      hue: hue,
      saturation: Math.round(s),
      value: Math.round(v)
    }
  }

  static HSLtoCMYK(hue: number, saturation: number, luminosity: number) {
    const rgb = this.HSLtoRGB(hue, saturation, luminosity)
    return this.RGBtoCMYK(rgb.red, rgb.green, rgb.blue)
  }


  static HSVtoHEX(hue: number, saturation: number, value: number) {
    const rgb = this.HSVtoRGB(hue, saturation, value)
    return this.RGBtoHEX(rgb.red, rgb.green, rgb.blue)
  }

  static HSVtoRGB(hue: number, saturation: number, value: number) {
    const hsl = this.HSVtoHSL(hue, saturation, value)
    return this.HSLtoRGB(hsl.hue, hsl.saturation, hsl.luminosity)
  }

  static HSVtoHSL(hue: number, saturation: number, value: number) {
    if (hue < 0 || hue > 360 || saturation < 0 || saturation > 100 || value < 0 || value > 100)
      throw `Color values [${hue}, ${saturation}%, ${value}%] are not in valid ranges.`

    const l = value * (1 - (saturation / 2))

    const s = (l === 0 || l === 1) ? 0 :
      ((value - l) / Math.min(l, 1 - l))

    return {
      hue: hue,
      saturation: Math.round(s),
      luminosity: Math.round(l)
    }
  }

  static HSVtoCMYK(hue: number, saturation: number, value: number) {
    const rgb = this.HSVtoRGB(hue, saturation, value)
    return this.RGBtoCMYK(rgb.red, rgb.green, rgb.blue)
  }


  static CMYKtoHEX(cyan: number, magenta: number, yellow: number, key: number) {
    const rgb = this.CMYKtoRGB(cyan, magenta, yellow, key)
    return this.RGBtoHEX(rgb.red, rgb.green, rgb.blue)
  }

  static CMYKtoRGB(cyan: number, magenta: number, yellow: number, key: number) {
    if (cyan < 0 || cyan > 100 || magenta < 0 || magenta > 100 || yellow < 0 || yellow > 100 || key < 0 || key > 100)
      throw `[${cyan}, ${magenta}, ${yellow}, ${key}] is not in valid format.`

    const c = cyan / 100
    const m = magenta / 100
    const y = yellow / 100
    const k = key / 100

    const r = (1 - c) * (1 - k)
    const g = (1 - m) * (1 - k)
    const b = (1 - y) * (1 - k)

    return {
      red: Math.round(255 * r),
      green: Math.round(255 * g),
      blue: Math.round(255 * b),
    }
  }

  static CMYKtoHSL(cyan: number, magenta: number, yellow: number, key: number) {
    const rgb = this.CMYKtoRGB(cyan, magenta, yellow, key)
    return this.RGBtoHSL(rgb.red, rgb.green, rgb.blue)
  }

  static CMYKtoHSV(cyan: number, magenta: number, yellow: number, key: number) {
    const rgb = this.CMYKtoRGB(cyan, magenta, yellow, key)
    return this.RGBtoHSV(rgb.red, rgb.green, rgb.blue)
  }

}

import {Shade} from "../models/shade.model";
import {Color} from "../models/color.model";

export class ColorInterpolater {

  /**
   * Regenerate every shade but the fixed ones
   * @param color
   */
  public static regenerateShades(color: Color) {
    let shades = [...color.shades]

    // clear and sort shades
    shades = shades.filter(shade => shade.fixed)
    shades.sort((a, b) => b.luminosity - a.luminosity)

    // set new indices
    let indices = [...Array(10).keys()].map(index => 20 + index * 80)
    indices[0] = 50
    for (const shade of shades) {
      const index = indices.reduce((prev, curr) =>
        (Math.abs(curr - (100 - shade.brightness) * 10) < Math.abs(prev - (100 - shade.brightness) * 10) ? curr : prev))
      shade.setIndex(index)
      indices = indices.filter(i => i !== index)
    }

    // add white and black to shades
    shades.push(new Shade(1000, true,
      shades[shades.length-1].hue,
      Math.max(shades[shades.length-1].saturation - 10, 0),
      0)
    )
    shades.push(new Shade(0, true,
      shades[0].hue,
      Math.min(shades[0].saturation + 10, 100),
      100)
    )
    shades.sort((a, b) => a.index - b.index)

    // generate missing shades
    for (const index of indices) {
      const smaller = [...shades].reverse().find(shade => shade.index < index && shade.fixed) || shades[0]
      const bigger = shades.find(shade => shade.index > index && shade.fixed) || shades[shades.length-1]

      const hue = this.mapNumbers(index, smaller.index, bigger.index,
        bigger.hue - smaller.hue > 180 ? (smaller.hue + 360) : smaller.hue,
        smaller.hue - bigger.hue > 180 ? (bigger.hue + 360) : bigger.hue
      ) % 360
      const saturation = this.mapNumbers(index, smaller.index, bigger.index, smaller.saturation, bigger.saturation)
      const luminosity = this.mapNumbers(index, smaller.index, bigger.index, smaller.luminosity, bigger.luminosity)

      shades.push(new Shade(index, false, hue, saturation, luminosity))
    }

    // remove white and black and set shades to color
    shades = shades.filter(shade => shade.index !== 0 && shade.index !== 1000)

    // repair broken orders
    shades.sort((a, b) => b.luminosity - a.luminosity)
    indices = [...Array(10).keys()].map(index => index * 100)
    indices[0] = 50
    for (let i = 0; i < shades.length; i++) {
      shades[i].setIndex(indices[i])
    }

    color.shades = shades
  }

  /**
   * Translate x in [in_min to in_max] to y in [out_min to out_max]
   * @param x
   * @param in_min
   * @param in_max
   * @param out_min
   * @param out_max
   * @private
   */
  private static mapNumbers(x: number, in_min: number, in_max: number, out_min: number, out_max: number): number {
    return Math.round((x - in_min) * (out_max - out_min) / (in_max - in_min) + out_min)
  }

}

import {EventEmitter} from "@angular/core";
import {Dialog} from "../interfaces/dialog.interface";
import {ToUnicodeVariantUtil} from "../utils/to-unicode-variant.util";

export class CssCopyDialog {

  constructor(
    private notification: EventEmitter<Dialog | undefined>,
  ) { }

  getNotification(): Dialog {
    const closeEmitter = new EventEmitter()
    closeEmitter.subscribe(() => {
      this.notification.emit(undefined)
    })

    const docEmitter = new EventEmitter()
    docEmitter.subscribe(() => {
      window.open('https://developer.mozilla.org/en-US/docs/Web/CSS/Using_CSS_custom_properties#using_the_root_pseudo-class', '_blank')
    })

    return {
      message: 'The palette has been copied to your clipboard. ' +
        `To use the colors as CSS properties copy the contents of the clipboard to the ${ToUnicodeVariantUtil.toUnicodeVariant(':root', 'm')} of your css file. ` +
        'Now you can use them by referencing them like:\n' +
        ToUnicodeVariantUtil.toUnicodeVariant('color: var(--color-100);', 'm'),
      actions: [{
        text: 'Read more',
        title: 'Open MDN Web Docs',
        action: docEmitter
      }]
    }
  }

}

import {EventEmitter} from "@angular/core";
import {PaletteExporter} from "../class/palette-exporter";
import {Palette} from "../models/palette.model";
import {Dialog} from "../interfaces/dialog.interface";
import {CssCopyDialog} from "./css-copy.dialog";
import {CssFileDialog} from "./css-file.dialog";

export class CssDialog {

  constructor(
    private notification: EventEmitter<Dialog | undefined>,
    private palette: Palette
  ) { }

  getNotification() {
    const cssCopyEmitter = new EventEmitter()
    cssCopyEmitter.subscribe(() => {
      const css = PaletteExporter.exportPaletteToCSS(this.palette)
      navigator.clipboard.writeText(css).then(() => {
        this.notification.emit(new CssCopyDialog(
          this.notification
        ).getNotification())
      }).catch(e => {
        this.notification.emit({
          message: `An error occurred while copying to the clipboard\n\n${e}`,
          actions: []
        })
      })
    })

    const cssFileEmitter = new EventEmitter()
    cssFileEmitter.subscribe(() => {
      const css = PaletteExporter.exportCssFile(this.palette)
      const blob = new Blob([css], {type: 'text/css'})

      const a = document.createElement('a')
      a.href = URL.createObjectURL(blob)
      a.download = 'colors.css'
      a.click()

      this.notification.emit(new CssFileDialog(
        this.notification
      ).getNotification())
    })

    return {
      message: 'Do you want to copy the CSS properties into your own files or create a new one?',
      actions: [{
        text: 'Copy',
        title: 'Copy content in own CSS file',
        action: cssCopyEmitter
      }, {
        text: 'File',
        title: 'Download a new file',
        action: cssFileEmitter
      }]
    }
  }

}

import {EventEmitter} from "@angular/core";
import {Dialog} from "../interfaces/dialog.interface";
import {ToUnicodeVariantUtil} from "../utils/to-unicode-variant.util";

export class CssFileDialog {

  constructor(
    private notification: EventEmitter<Dialog | undefined>,
  ) { }

  getNotification(): Dialog {
    const closeEmitter = new EventEmitter()
    closeEmitter.subscribe(() => {
      this.notification.emit(undefined)
    })

    const docEmitter = new EventEmitter()
    docEmitter.subscribe(() => {
      window.open('https://developer.mozilla.org/en-US/docs/Web/CSS/Using_CSS_custom_properties#using_the_root_pseudo-class', '_blank')
    })

    return {
      message: 'Move the downloaded colors.css file to the root of your webserver. ' +
        'Then link it in the head of your html file like:\n' +
        ToUnicodeVariantUtil.toUnicodeVariant('<link rel="stylesheet" href="/colors.css">', 'm') + '\n\n' +
        'Now you can use them by referencing them like:\n' +
