  }, {
    link: '/edit',
    title: 'Edit your palette',
    text: 'Edit',
    icon: '<path fill="currentColor" d="M7.2 39.05q-.85 0-1.45-.6t-.6-1.5q0-.85.6-1.45t1.45-.6h9.35q.85 0 1.475.6.625.6.625 1.5 0 .85-.625 1.45t-1.475.6Zm0-25.95q-.85 0-1.45-.6t-.6-1.5q0-.85.6-1.45t1.45-.6h17.65q.85 0 1.475.6.625.6.625 1.5 0 .85-.625 1.45t-1.475.6Zm15.95 30.05q-.85 0-1.475-.6-.625-.6-.625-1.5V32.8q0-.85.625-1.45t1.475-.6q.85 0 1.45.6t.6 1.45v2.1h15.6q.85 0 1.45.6t.6 1.5q0 .85-.6 1.45t-1.45.6H25.2v2q0 .9-.6 1.5t-1.45.6Zm-6.6-13q-.85 0-1.45-.6t-.6-1.45v-2.05H7.2q-.85 0-1.45-.6T5.15 24q0-.85.6-1.45t1.45-.6h7.3V19.8q0-.85.6-1.45t1.45-.6q.85 0 1.475.6.625.6.625 1.45v8.3q0 .85-.625 1.45t-1.475.6Zm6.6-4.1q-.85 0-1.475-.6-.625-.6-.625-1.45t.625-1.45q.625-.6 1.475-.6H40.8q.85 0 1.45.6t.6 1.45q0 .85-.6 1.45t-1.45.6Zm8.3-8.8q-.85 0-1.475-.6-.625-.6-.625-1.45V6.95q0-.9.625-1.5t1.475-.6q.85 0 1.45.6t.6 1.5v2h7.3q.85 0 1.45.6t.6 1.5q0 .85-.6 1.45t-1.45.6h-7.3v2.1q0 .85-.6 1.45t-1.45.6Z"/>',
  }, {
    link: '/preview',
    title: 'Preview your palette with components',
    text: 'Preview',
    icon: '<path fill="currentColor" d="M11 37h12v-9H11Zm0-11h12V11H11Zm14 11h12V22H25Zm0-17h12v-9H25ZM9 42q-1.2 0-2.1-.9Q6 40.2 6 39V9q0-1.2.9-2.1Q7.8 6 9 6h30q1.2 0 2.1.9.9.9.9 2.1v3h3v3h-3v7.5h3v3h-3V33h3v3h-3v3q0 1.2-.9 2.1-.9.9-2.1.9Zm0-3h30V9H9v30ZM9 9v30V9Z"/>',
  }]

  constructor(
    private storage: StorageService,
    public colorService: ColorService,
    public paletteService: PaletteService,
    public router: Router
  ) {
    // Redirect to https if server served / browser fetched with http
    if (environment.production && window.location.href.startsWith('http://'))
      window.location.href = 'https://' + window.location.href.substring(6)

    // Load theme from local storage and subscribe to changes
    this.dark = storage.loadTheme()
    storage.darkEmitter.subscribe(d => this.dark = d.valueOf())
  }

}

import {Component, Input, OnInit} from '@angular/core';
import {Color} from "../../models/color.model";
import {Shade} from "../../models/shade.model";
import {ChangeType, ColorService} from "../../services/color.service";
import {ToUnicodeVariantUtil} from "../../utils/to-unicode-variant.util";
import {NotificationService} from "../../services/notification.service";

@Component({
  selector: 'color-editor',
  templateUrl: './color-editor.component.html',
  styleUrls: ['./color-editor.component.css']
})
export class ColorEditorComponent implements OnInit {

  @Input()
  dark = false

  shade: Shade | undefined
  color: Color | undefined

  constructor(
    public colorService: ColorService,
    private notificationService: NotificationService
  ) {
    this.colorService.getColorChangeEmitter().subscribe(changeType => {
      if (changeType === ChangeType.LOAD) {
        this.color = this.colorService.getColor()
        this.shade = this.colorService.getShade()
      }

      this.updateProperties()
    })
  }

  ngOnInit(): void {
    this.updateProperties()
  }

  /**
   * Update current shade. The property with type is changed to the new value.
   * @param type Property to change
   * @param value Value to change to
   */
  updateColor(type: UpdateType, value: string | number) {
    if (!this.shade)
      return

    if (type === UpdateType.HEX && isNaN(+value)) {
      if (`${value}`.match(/^#[0-9A-Fa-f]{6}$/))
        this.shade.setHEX(`${value}`, true)
    } else if (!isNaN(+value)) {
      value = parseInt(`${value}`)
      if (type === UpdateType.HUE)
        this.shade.setHSL(this.wheelToHue(value), this.shade.saturation, this.shade.luminosity, true)
      else if (type === UpdateType.SATURATION)
        this.shade.setHSL(this.shade.hue, value, this.shade.luminosity, true)
      else if (type === UpdateType.LUMINOSITY)
        this.shade.setHSL(this.shade.hue, this.shade.saturation, 100 - value, true)
    }

    this.colorService.adjustShades()
  }

  /**
   * Update all css properties to the values of the current selected shade.
   */
  updateProperties() {
    if (!this.shade)
      return

    document.documentElement.style.setProperty('--selected-hex', this.shade.hex)
    document.documentElement.style.setProperty('--selected-hue', String(this.shade.hue))
    document.documentElement.style.setProperty('--selected-saturation', this.shade.saturation + "%")
    document.documentElement.style.setProperty('--selected-luminosity', this.shade.luminosity + "%")
  }

  /**
   * Change a shade to be edited
   * @param shadeIndex
   */
  changeShade(shadeIndex: number) {
    if (this.color)
      this.colorService.loadColor(this.color, shadeIndex)
  }

  /**
   * Release a shade and let it be interpolated instead
   * @param shade
   * @param $event
   */
  releaseShade(shade: Shade, $event: MouseEvent) {
    $event.preventDefault()
    if (!this.color)
      return

    if (this.color.shades.filter(s => s.fixed).length > 1) {
      shade.fixed = false
      this.colorService.adjustShades()
    }
  }

  /**
   * Calculate from color wheel degree to hue
   * @param wheel
   */
  wheelToHue(wheel: number) {
    let newHue
    if (wheel < 120)
      newHue = .5 * wheel
    else if (wheel < 180)
      newHue = wheel + 300
    else if (wheel < 240)
      newHue = 2 * wheel + 120
    else
      newHue = wheel

    return newHue % 360
  }

  /**
   * Calculate from hue to color wheel degree
   * @param hue
   */
  hueToWheel(hue: number) {
    let wheel
    if (hue < 60)
      wheel = 2 * hue
    else if (hue < 120)
      wheel = hue + 60
    else if (hue < 240)
      wheel = .5 * hue + 120
    else
      wheel = hue

    return wheel % 360
  }

}

enum UpdateType {
  HEX, HUE, SATURATION, LUMINOSITY
}

