import {ColorInterpolater} from "../class/color-interpolater";
import {Shade} from "./shade.model";

export class Color {

  name: string

  shades: Shade[]

  constructor(
    name: string,
    hex: string
  ) {
    if (!hex.startsWith('#') || hex.length !== 7)
      throw `Color '${hex}' is not in form #RRGGBB.`

    this.name = name
    this.shades = ColorInterpolater.interpolateShades(hex)

    this.shades.sort((a, b) => a.index - b.index)
  }

  /**
   * Get shade by index (50, 100, 200, ..., 900)
   * @param index
   */
  public getShade(index: number): Shade {
    return this.shades.find(s => s.index === index) || this.shades[0]
  }

  /**
   * Generate a random color with all shades.
   */
  public static generateRandomColor(): Color {
    const shade = Shade.generateRandomShade()
    return new Color(
      shade.hex,
      shade.hex
    )
  }

  /**
   * Stringify the color.
   */
  public toString() {
    return JSON.stringify({
      name: this.name,
      shades: this.shades
    })
  }

  /**
   * Parse the json object to a color.
   * Throws exception if json object is no valid color object.
   * @param json
   */
  public static parseColor(json: any): Color {
    if (!json.name)
      throw 'Color has no name'
    if (!json.shades || json.shades.length < 6)
      throw 'Color has not enough shades'

    return new Color(json.name, json.shades[5].hex)
  }

}

export class ColorConverter {

  /**
   * Convert from HEX string (#RRGGBB) to HSL format.
   * @param hex HEX color sting (#RRGGBB)
   * @constructor
   */
  static HEXtoHSL(hex: string) {
    if (!hex.startsWith('#') || hex.length !== 7)
      throw `Color '${hex}' is not in form #RRGGBB.`

    const rgb = this.HEXtoRGB(hex)
    const r = rgb.red / 255
    const g = rgb.green / 255
    const b = rgb.blue / 255

    const cMax = Math.max(r, g, b)
    const cMin = Math.min(r, g, b)
    const d = cMax - cMin

    const l = this.getLuminosity(cMax, cMin)
    const s = this.getSaturation(d, l)
    let h = this.getHue(d, cMax, r, g, b)
    if (h < 0)
      h += 360

    return {
      hue: Math.round(h),
      saturation: Math.round(s),
      luminosity: Math.round(l)
    }
  }

  /**
   * Get luminosity for HSL format.
   * @param cMax Max value from [red, green, blue] / 255
   * @param cMin Min value from [red, green, blue] / 255
   * @private
   */
  private static getLuminosity(cMax: number, cMin: number): number {
    return (cMax + cMin) * 50
  }

  /**
   * Get saturation for HSL format.
   * @param delta Difference between max and min values from [red, green, blue] / 255
   * @param luminosity Luminosity from HSL format.
   * @private
   */
  private static getSaturation(delta: number, luminosity: number): number {
    if (delta === 0)
      return 0
    else
      return 100 * delta / (1 - Math.abs(2 * (luminosity / 100) - 1))
  }

  /**
   * Get hue for HSL or HSB format.
   * @param delta Difference between max and min values from [red, green, blue] / 255
   * @param cMax Max value from [red, green, blue] / 255
   * @param red Red value between 0 - 1
   * @param green Green value between 0 - 1
   * @param blue Blue value between 0 - 1
   * @private
   */
  private static getHue(delta: number, cMax: number, red: number, green: number, blue: number): number {
    if (delta === 0)
      return 0
    else if (cMax === red)
      return 60 * ( ( (green - blue) / delta) % 6)
    else if (cMax === green)
      return 60 * ( ( (blue - red) / delta) + 2)
    else
      return 60 * ( ( (red - green) / delta) + 4)
  }

  /**
   * Convert color from HSL format into HEX string.
   * @param hue Hue between 0 - 360
   * @param saturation Saturation between 0 - 100
   * @param lightness Lightness between 0 - 100
   * @constructor
   */
  static HSLtoHEX(hue: number, saturation: number, lightness: number): string {
    if (hue < 0 || hue > 360 || saturation < 0 || saturation > 100 || lightness < 0 || lightness > 100)
      throw `Color values [${hue}Â°, ${saturation}%, ${lightness}%] are not in valid ranges.`

    const h = hue
    const s = saturation / 100
    const l = lightness / 100

    const c = (1 - Math.abs(2 * l - 1)) * s
    const x = c * (1 - Math.abs((h / 60) % 2 - 1))
    const m = l - c / 2

    const r = (h < 60 || h >=300) ? c : (h < 120 || h >= 240) ? x : 0
    const g = (h >= 240) ? 0 : (h < 60 || h >= 180) ? x : c
    const b = (h < 120) ? 0 : (h < 180 || h >= 300) ? x : c

    const R = Math.round((r + m) * 255)
    const G = Math.round((g + m) * 255)
    const B = Math.round((b + m) * 255)

    return `#${(R < 16 ? 0 : '') + R.toString(16)}${(G < 16 ? 0 : '') + G.toString(16)}${(B < 16 ? 0 : '') + B.toString(16)}`
  }

  /**
   * Convert from HEX string to RGB values.
   * @param hex HEX color string (#RRGGBB)
   * @constructor
   */
  static HEXtoRGB(hex: string) {
    if (!hex.startsWith('#') || hex.length !== 7)
      throw `Color ${hex} is not in form #RRGGBB.`

    return {
      red: parseInt(hex.substring(1, 3), 16),
      green: parseInt(hex.substring(3, 5), 16),
      blue: parseInt(hex.substring(5, 7), 16)
    }
  }

}

import {ColorConverter} from "./color-converter";
import {Shade} from "../models/shade.model";

export class ColorInterpolater {

  /**
   * Interpolate 10 shades starting with one HEX color string.
   * @param hex HEX color string (#RRGGBB)
   */
  static interpolateShades(hex: string) {
    if (!hex.startsWith('#') || hex.length !== 7)
      throw `Color '${hex}' is not in form #RRGGBB.`

    const shades: Shade[] = []
    const hsl = ColorConverter.HEXtoHSL(hex)

    if (100 - hsl.luminosity < 7.5) {
      shades.push(new Shade(50, hex))
      this.generateDarkerColors(hsl, 0, shades)
    } else if (100 - hsl.luminosity >= 85) {
      shades.push(new Shade(900, hex))
      this.generateLighterColors(hsl, 900, shades)
    } else {
      let index = 100
      while (100 - hsl.luminosity > (index / 10) + 5)
        index += 100
      shades.push(new Shade(index, hex))
      this.generateLighterColors(hsl, index, shades)
      this.generateDarkerColors(hsl, index, shades)
    }

    return shades
  }

  /**
   * Interpolate all lighter shades from HSL color.
   * @param hsl Color in HSL format
   * @param index Index of HSL color shade
   * @param shades Array to add all generated shades
   * @private
   */
  private static generateLighterColors(hsl: { saturation: number; hue: number; luminosity: number }, index: number, shades: Shade[]) {
    const step = index / 100
    let i = 1

    for (; i < step; i++) {
      const interpolatedLuminosity = hsl.luminosity + (i / step) * (100 - hsl.luminosity)
      const newIndex = index - i * 100
      shades.push(new Shade(newIndex, hsl.hue, hsl.saturation, interpolatedLuminosity))
    }

    const interpolatedLuminosity = hsl.luminosity + ((2 * i - 1) / (2 * step)) * (100 - hsl.luminosity)
    shades.push(new Shade(50, hsl.hue, hsl.saturation, interpolatedLuminosity))
  }

  /**
   * Interpolate all darker shades from HSL color.
   * @param hsl Color in HSL format
   * @param index Index of HSL color shade
   * @param shades Array to add all generated shades
   * @private
   */
  private static generateDarkerColors(hsl: { saturation: number; hue: number; luminosity: number }, index: number, shades: Shade[]) {
    const step = (1000 - index) / 100
    let i = 1

    for (; i < step; i++) {
      const interpolatedLuminosity = hsl.luminosity - (i / step) * hsl.luminosity
      const newIndex = index + i * 100
      shades.push(new Shade(newIndex, hsl.hue, hsl.saturation, interpolatedLuminosity))
    }
  }

}

import {Color} from "./color.model";
// @ts-ignore
import {v4 as uuidv4} from "uuid";

export class Palette {

  title: string
  id: string
  colors: Color[] = []
  order = false

  constructor(title: string, id: string) {
    this.title = title
    this.id = id
  }

  /**
   * Add a color to the beginning of the palette.
   * @param color
   */
  public addColor(color: Color) {
    if (this.colors.find(c => c.name === color.name))
      this.colors[this.colors.findIndex(c => c.name === color.name)] = color
    else
      this.colors.unshift(color)

    this.order = false
  }

  /**
   * Remove the color from the palette
   * @param color
   */
  public removeColor(color: Color) {
    const index = this.colors.indexOf(color)
    if (index > -1) {
      this.colors.splice(index, 1)
      return true
    } else {
      return false
    }
  }

  /**
   * Sort all colors of the palette by hue
   */
  public sortColors() {
    this.colors.sort((a, b) => {
      if (this.order)
        return a.getShade(500).hue - b.getShade(500).hue
      else
        return b.getShade(500).hue - a.getShade(500).hue
    })
    this.order = !this.order
  }

  /**
   * Generate a new random palette with 'size' colors.
   * @param size
   */
  public static generateRandomPalette(size: number): Palette {
    if (size < 1)
      throw `Size was ${size} but must be positive.`

    const palette = new Palette('Random', uuidv4())
    for (let i = 0; i < size; i++) {
      const color = Color.generateRandomColor()
      palette.addColor(new Color(color.name, color.getShade(500).hex))
    }

    palette.colors.sort((a, b) => a.getShade(500).hue - b.getShade(500).hue)

    return palette;
  }

  /**
   * Stringify the palette.
   */
  public toString() {
    return JSON.stringify({
      id: this.id,
      title: this.title,
      colors: this.colors
    })
  }

  /**
   * Parse the json object to a palette.
   * Throws exception if not all values are given.
   * @param json
   */
  public static parsePalette(json: any): Palette {
    if (!json.title)
      throw 'Palette has no title'
    if (!json.id)
      throw 'Palette has no id'
    if (!json.colors)
      throw 'Palette has no colors'

    const palette = new Palette(json.title, json.id)

    for (const color of json.colors) {
      palette.addColor(Color.parseColor(color))
    }
    palette.colors.reverse()

    return palette
  }

}

import {ColorConverter} from "../class/color-converter";

export class Shade {

  index: number
  hex: string
  hue: number
  saturation: number
  luminosity: number

  public constructor(index: number, hex: string);
  public constructor(index: number, hue: number, saturation: number, luminosity: number);
  public constructor(index: number, hex: string, hue: number, saturation: number, luminosity: number);

  constructor(...args: any[]) {
    this.index = args[0]

    if (args.length === 2) {
      this.hex = args[1]
      const hsl = ColorConverter.HEXtoHSL(this.hex)
      this.hue = hsl.hue
      this.saturation = hsl.saturation
      this.luminosity = hsl.luminosity
    } else if (args.length === 4) {
      this.hex = ColorConverter.HSLtoHEX(args[1], args[2], args[3])
      this.hue = args[1]
      this.saturation = args[2]
      this.luminosity = args[3]
    } else {
      this.hex = args[1]
      this.hue = args[2]
      this.saturation = args[3]
      this.luminosity = args[4]
    }
  }

  /**
   * Generate a random shade.
   */
  public static generateRandomShade(): Shade {
    return new Shade(
      0,
      Math.floor(Math.random() * 360),
      30 + Math.floor(Math.random() * 60),
      25 + Math.floor(Math.random() * 50)
    )
  }

}

export class ToUnicodeVariantUtil {

  static offsets: any = {
    m: [0x1d670, 0x1d7f6],
    b: [0x1d400, 0x1d7ce],
    i: [0x1d434, 0x00030],
    bi: [0x1d468, 0x00030],
    c: [0x0001d49c, 0x00030],
    bc: [0x1d4d0, 0x00030],
    g: [0x1d504, 0x00030],
    d: [0x1d538, 0x1d7d8],
    bg: [0x1d56c, 0x00030],
    s: [0x1d5a0, 0x1d7e2],
    bs: [0x1d5d4, 0x1d7ec],
    is: [0x1d608, 0x00030],
    bis: [0x1d63c, 0x00030],
    o: [0x24B6, 0x2460],
    on: [0x0001f150, 0x2460],
    p: [0x249c, 0x2474],
    q: [0x1f130, 0x00030],
    qn: [0x0001F170, 0x00030],
    w: [0xff21, 0xff10],
    u: [0x2090, 0xff10]
  }

  static variantOffsets: any = {
    'monospace': 'm',
    'bold' : 'b',
    'italic' : 'i',
    'bold italic' : 'bi',
    'script': 'c',
    'bold script': 'bc',
    'gothic': 'g',
    'gothic bold': 'bg',
    'doublestruck': 'd',
    'sans': 's',
    'bold sans' : 'bs',
    'italic sans': 'is',
    'bold italic sans': 'bis',
    'parenthesis': 'p',
    'circled': 'o',
    'circled negative': 'on',
    'squared': 'q',
    'squared negative': 'qn',
    'fullwidth': 'w'
  }

  //special characters (absolute values)
  static special: any = {
    m: {
      ' ': 0x2000,
      '-': 0x2013
    },
    i: {
      'h': 0x210e
    },
    g: {
      'C': 0x212d,
      'H': 0x210c,
      'I': 0x2111,
      'R': 0x211c,
      'Z': 0x2128
    },
    d: {
      'C': 0x2102,
      'H': 0x210D,
      'N': 0x2115,
      'P': 0x2119,
      'Q': 0x211A,
      'R': 0x211D,
      'Z': 0x2124
    },
    o: {
      '0': 0x24EA,
      '1': 0x2460,
      '2': 0x2461,
      '3': 0x2462,
      '4': 0x2463,
      '5': 0x2464,
      '6': 0x2465,
      '7': 0x2466,
      '8': 0x2467,
      '9': 0x2468,
    },
    on: {},
    p: {},
    q: {},
    qn: {},
    w: {}
  }

  static chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz'

  static numbers = '0123456789'


  public static toUnicodeVariant(str: string, variant: string, flags?: string) {
    if (Object.keys(this.special.p).length === 0) {
      //support for parenthesized latin letters small cases
      //support for full width latin letters small cases
      //support for circled negative letters small cases
      //support for squared letters small cases
      //support for squared letters negative small cases
      ['p', 'w', 'on', 'q', 'qn'].forEach(t => {
        for (let i = 97; i <= 122; i++) {
          this.special[t][String.fromCharCode(i)] = this.offsets[t][0] + (i-97)
        }
      })
    }

    const type = this.getType(variant)
    const underline = this.getFlag('underline|u', flags)
    const strike = this.getFlag('strike|s', flags)
    let result = ''

    for (let c of str) {
      let index
      if (this.special[type] && this.special[type][c]) c = String.fromCodePoint(this.special[type][c])
      if (type && (index = this.chars.indexOf(c)) > -1) {
        result += String.fromCodePoint(index + this.offsets[type][0])
      } else if (type && (index = this.numbers.indexOf(c)) > -1) {
        result += String.fromCodePoint(index + this.offsets[type][1])
      } else {
        result += c
      }
      if (underline) result += '\u0332' // add combining underline
      if (strike) result += '\u0336' // add combining strike
    }
    return result
  }

  private static getType(variant: string) {
    if (this.variantOffsets[variant])
      return this.variantOffsets[variant]
    if (this.offsets[variant])
      return variant
    return 'm' //monospace as default
  }

  private static getFlag(flag: string, flags?: string) {
    if (!flags)
      return false
    return flag.split('|').some(f => flags.split(',').indexOf(f) > -1)
  }

}

