import {Component, EventEmitter} from '@angular/core';
import {Palette} from "./models/palette.model";
import {Shade} from "./models/shade.model";
import {Color} from "./models/color.model";
import {environment} from "../environments/environment";
import {StorageService} from "./services/storage.service";
// @ts-ignore
import {v4 as uuidv4} from "uuid";
import {ColorService} from "./services/color.service";

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
})
export class AppComponent {

  title = 'tailwind-color-generator'
  version = environment.version
  dark

  shade: Shade = Shade.generateRandomShade()
  color: Color
  palette: Palette

  editColor = new EventEmitter<Color>()

  constructor(
    private storage: StorageService,
    public colorService: ColorService
  ) {
    // Redirect to https if server served / browser fetched with http
    if (environment.production && window.location.href.startsWith('http://'))
      window.location.href = 'https://' + window.location.href.substring(6)

    // Load theme from local storage and subscribe to changes
    this.dark = storage.loadTheme()
    storage.darkEmitter.subscribe(d => this.dark = d.valueOf())

    // Load palette if saved in local storage
    this.palette = storage.loadPalette()

    // Load random color for editor
    this.color = Color.generateRandomColor()
  }

  /**
   * Set and display a new random palette with 5 - 10 colors.
   */
  randomPalette() {
    this.palette = Palette.generateRandomPalette(Math.floor(5 + Math.random() * 5))
  }

  /**
   * Remove the current palette from logic.
   * The palette can be restored if it was saved in local storage.
   */
  removePalette() {
    this.palette = new Palette('Palette', uuidv4())
  }

  /**
   * Add color to the current palette.
   * If no palette exists, a new one is created.
   * @param color
   */
  addColor(color: Color | undefined) {
    if (color) {
      if (!this.palette)
        this.palette = new Palette('Palette', uuidv4())
      this.palette.addColor(color)
    }
  }

}

import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import {Color} from "../../models/color.model";
import {Shade} from "../../models/shade.model";
import {ChangeType, ColorService} from "../../services/color.service";

@Component({
  selector: 'color-editor',
  templateUrl: './color-editor.component.html',
  styleUrls: ['./color-editor.component.css']
})
export class ColorEditorComponent implements OnInit {

  @Input()
  dark = false

  @Output()
  addColor = new EventEmitter<Color>()

  shade: Shade
  state: EditorState = EditorState.ADD

  constructor(
    public colorService: ColorService
  ) {
    this.shade = this.colorService.getColor().getShade(500)

    this.colorService.getColorChangeEmitter().subscribe(changeType => {
      if (changeType !== ChangeType.ADJUST) {
        this.shade = this.colorService.getColor().getShade(500)
        this.state = changeType === ChangeType.RANDOM ? EditorState.ADD : EditorState.EDIT
      }
      this.updateProperties()
    })
  }

  ngOnInit(): void {
    if (document.body.classList.contains('dark'))
      this.dark = true

    this.updateProperties()
  }

  /**
   * Update current shade. The property with type is changed to the new value.
   * @param type Property to change
   * @param value Value to change to
   */
  updateColor(type: UpdateType, value: string | number) {
    if (type === UpdateType.HEX && isNaN(+value)) {
      this.shade = new Shade(0, `${value}`)
    } else if (!isNaN(+value)) {
      value = parseInt(`${value}`)
      if (type === UpdateType.HUE) {
        this.shade = new Shade(0, value, this.shade.saturation, this.shade.luminosity)
      } else if (type === UpdateType.SATURATION) {
        this.shade = new Shade(0, this.shade.hue, value, this.shade.luminosity)
      } else if (type === UpdateType.LUMINOSITY) {
        this.shade = new Shade(0, this.shade.hue, this.shade.saturation, 100 - value)
      }
    }

    this.updateProperties()
  }

  adjustColor() {
