import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import {Color} from "../../models/color.model";
import {ColorService} from "../../services/color.service";

@Component({
  selector: 'color-viewer',
  templateUrl: './color-viewer.component.html',
})
export class ColorViewerComponent implements OnInit {

  @Input()
  color: Color
  @Input()
  dark = false

  @Output()
  onRemove = new EventEmitter<Color>()

  constructor(
    public colorService: ColorService
  ) {
    this.color = Color.generateRandomColor()
  }

  ngOnInit(): void {
    if (document.getElementById('body')?.classList.contains('dark'))
      this.dark = true
  }

}

import {Component, Input, OnInit} from '@angular/core';
import {StorageService} from "../../services/storage.service";

@Component({
  selector: 'light-switch',
  templateUrl: './light-switch.component.html',
})
export class LightSwitchComponent implements OnInit {

  @Input()
  dark: boolean | undefined

  constructor(
    private storage: StorageService
  ) { }

  ngOnInit(): void {
  }

  /**
   * Toggle between light and dark theme
   */
  toggleTheme(): void {
    this.dark = this.storage.toggleTheme(!this.dark)
  }

}

import {Component, ElementRef, EventEmitter, Input, OnInit, Output, ViewChild} from '@angular/core';
import {Palette} from "../../models/palette.model";
import {ToUnicodeVariantUtil} from "../../utils/to-unicode-variant.util";
import {Color} from "../../models/color.model";
import {StorageService} from "../../services/storage.service";

@Component({
  selector: 'palette-viewer',
  templateUrl: './palette-viewer.component.html',
})
export class PaletteViewerComponent implements OnInit {

  @Input()
  palette: Palette

  @Input()
  dark = false

  @Output()
  onRemove = new EventEmitter<Event>()

  editingState = false

  @ViewChild('editTitle')
  editTitle: ElementRef<HTMLInputElement> | undefined

  constructor(
    private storage: StorageService
  ) {
    this.palette = Palette.generateRandomPalette(5)
  }

  ngOnInit(): void {
  }

  /**
   * Ask user for confirmation an trigger onRemove event handler.
   * @param $event MouseEvent
   */
  removePalette($event: MouseEvent) {
    if (confirm(`Are you sure you want to delete the palette?\nIt can ${ToUnicodeVariantUtil.toUnicodeVariant('not', 'bs')} be restored.`))
      this.onRemove.emit($event)
  }

  /**
   * Remove color from palette and save the current palette to local storage.
   * If color is not present in palette nothing happens.
   * @param color Color to remove from palette
   */
  removeColor(color: Color) {
    if (this.palette.removeColor(color))
      this.savePalette()
  }

  /**
   * Save current palette to local storage.
   */
  savePalette() {
    this.storage.savePalette(this.palette)
  }

  /**
   * Open editor for palette name.
   */
  openEditor() {
    this.editingState = true
    setTimeout(() => {
      this.editTitle?.nativeElement.focus()
    }, 0)
  }

  /**
   * Close editor for palette name and save the palette to local storage.
   */
  closeEditor() {
    this.editingState = false
    if (this.palette)
      this.palette.title = this.editTitle?.nativeElement.value || 'Random'
    this.savePalette()
  }

  /**
   * Trigger palette sorting.
   */
  sortPalette() {
    this.palette.sortColors()
  }

}

import {Component, EventEmitter, Input, OnInit, Output} from '@angular/core';
import {Shade} from "../../models/shade.model";
import {Color} from "../../models/color.model";

@Component({
  selector: 'shade-picker',
  templateUrl: './shade-picker.component.html',
  styleUrls: ['./shade-picker.component.css']
})
export class ShadePickerComponent implements OnInit {

  @Input()
  shade: Shade | undefined

  @Input()
  dark = false

  @Input()
  name = ''

  @Output()
  colorEmitter = new EventEmitter<Color>()

  ngOnInit(): void {
    if (document.getElementById('body')?.classList.contains('dark'))
      this.dark = true

    this.updateProperties()
  }

  /**
   * Update current shade. The property with type is changed to the new value.
   * @param type Property to change
   * @param value Value to change to
   */
  updateShade(type: ChangeType, value: string | number): void {
    if (type === ChangeType.HEX && isNaN(+value))
      this.shade = new Shade(0, `${value}`)
    else if (!isNaN(+value)) {
      if (type === ChangeType.HUE) {
        // @ts-ignore
        this.shade = new Shade(0, value, this.shade?.saturation, this.shade?.luminosity)
      } else if (type === ChangeType.SATURATION) {
        // @ts-ignore
        this.shade = new Shade(0, this.shade?.hue, value, this.shade?.luminosity)
      } else if (type === ChangeType.LUMINOSITY) {
        // @ts-ignore
        this.shade = new Shade(0, this.shade?.hue, this.shade?.saturation, 100 - value)
      }
    }

    this.updateProperties()
  }

  /**
   * Update all css properties to the values of the current selected shade
   */
  updateProperties() {
    if (this.shade) {
      document.documentElement.style.setProperty('--selected-hex', this.shade.hex)
      document.documentElement.style.setProperty('--selected-hue', String(this.shade.hue))
      document.documentElement.style.setProperty('--selected-saturation', this.shade.saturation + "%")
      document.documentElement.style.setProperty('--selected-luminosity', this.shade.luminosity + "%")
    }
  }

  /**
   * Update the name of the current color / shade
   * @param name
   */
  updateName(name: string) {
    this.name = name
  }

  /**
   * Generate all shades for the current color.
   */
  generateColor() {
    if (this.shade)
      this.colorEmitter.emit(new Color(this.name || this.shade.hex, this.shade.hex))
  }

}


export enum ChangeType {
  HEX, HUE, SATURATION, LUMINOSITY
}

